import React, { useState } from "react";
import axios from "axios";
import { Rnd } from "react-rnd";
import { jsPDF } from "jspdf";
import { PDFDocument } from "pdf-lib";
import Tesseract from "tesseract.js";
import "./styles/App.css";

function App() {
  const [file, setFile] = useState(null);
  const [imageURL, setImageURL] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [filter, setFilter] = useState("none");
  const [filterIntensity, setFilterIntensity] = useState(100);
  const [compression, setCompression] = useState(100);
  const [texts, setTexts] = useState([]);
  const [selectedTextId, setSelectedTextId] = useState(null);
  const [showTextTools, setShowTextTools] = useState(false);
  const [ocrText, setOcrText] = useState("");
  const [scannedFiles, setScannedFiles] = useState([]);

  // ===== File Handling =====
  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    if (!uploadedFile) return;
    setFile(uploadedFile);
    setImageURL(URL.createObjectURL(uploadedFile));
    setProcessedImage(null);
    setTexts([]);
    setOcrText("");
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    if (!droppedFile) return;
    setFile(droppedFile);
    setImageURL(URL.createObjectURL(droppedFile));
    setProcessedImage(null);
    setTexts([]);
    setOcrText("");
  };

  const handleDragOver = (e) => e.preventDefault();

  // ===== Remove Background =====
  const handleRemoveBG = async () => {
    if (!file) return alert("Upload an image first");
    if (file.type.includes("pdf")) return alert("Remove.bg works only with images");

    const formData = new FormData();
    formData.append("image_file", file);
    try {
      const res = await axios.post(
        "https://api.remove.bg/v1.0/removebg",
        formData,
        { headers: { "X-Api-Key": "VSNXBaMjvgBp6NjjRheLV3cW" }, responseType: "blob" }
      );
      setProcessedImage(URL.createObjectURL(res.data));
    } catch (err) {
      alert("Remove.bg API error: " + err.message);
    }
  };

  // ===== Filters =====
  const getFilterStyle = () => {
    const i = filterIntensity;
    switch (filter) {
      case "grayscale": return { filter: `grayscale(${i}%)` };
      case "sepia": return { filter: `sepia(${i}%)` };
      case "invert": return { filter: `invert(${i}%)` };
      case "blur": return { filter: `blur(${i / 20}px)` };
      case "brightness": return { filter: `brightness(${i}%)` };
      case "contrast": return { filter: `contrast(${i}%)` };
      case "saturate": return { filter: `saturate(${i}%)` };
      case "hue-rotate": return { filter: `hue-rotate(${i * 3.6}deg)` };
      default: return {};
    }
  };

  // ===== Text Overlay =====
  const addText = (clickX = 50, clickY = 50) => {
    const newText = {
      id: Date.now(),
      text: "Click to edit",
      color: "#ff0000",
      fontSize: 24,
      x: clickX,
      y: clickY,
      width: 150,
      height: 40,
      rotation: 0,
      opacity: 1,
    };
    setTexts([...texts, newText]);
    setSelectedTextId(newText.id);
    setShowTextTools(true);
  };

  // Handle clicking on image to add text
  const handleImageClick = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    addText(x, y);
  };

  const updateText = (id, key, value) => {
    setTexts(texts.map((t) => (t.id === id ? { ...t, [key]: value } : t)));
  };

  const deleteText = (id) => {
    setTexts(texts.filter((t) => t.id !== id));
    if (selectedTextId === id) {
      setSelectedTextId(null);
      setShowTextTools(false);
    }
  };

  // Get selected text object
  const selectedText = texts.find((t) => t.id === selectedTextId);

  // ===== Generate Image =====
  const generateFinalImage = () => {
    return new Promise((resolve) => {
      const source = processedImage || imageURL;
      if (!source) return resolve(null);
      const img = new Image();
      img.crossOrigin = "anonymous";
      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");
        canvas.width = img.width;
        canvas.height = img.height;
        ctx.filter = getFilterStyle().filter || "none";
        ctx.drawImage(img, 0, 0);
        texts.forEach((t) => {
          ctx.save();
          ctx.translate(t.x + t.width / 2, t.y + t.height / 2);
          ctx.rotate((t.rotation * Math.PI) / 180);
          ctx.globalAlpha = t.opacity;
          ctx.font = `${t.fontSize}px Arial`;
          ctx.fillStyle = t.color;
          ctx.textAlign = "center";
          ctx.textBaseline = "middle";
          ctx.fillText(t.text, 0, 0);
          ctx.restore();
        });
        resolve(canvas.toDataURL("image/jpeg", compression / 100));
      };
      img.src = source;
    });
  };

  // ===== Download =====
  const handleDownloadImage = async (format) => {
    const finalDataURL = await generateFinalImage();
    if (!finalDataURL) return;
    if (format === "pdf") {
      const doc = new jsPDF();
      doc.addImage(finalDataURL, "JPEG", 10, 10, 190, 0);
      doc.save("docfixer.pdf");
    } else {
      const link = document.createElement("a");
      link.href = finalDataURL;
      link.download = `docfixer.${format}`;
      link.click();
    }
  };

  // ===== OCR =====
  const handleOCR = async () => {
    if (!file || !file.type.startsWith("image/")) return alert("Upload an image for OCR");
    setOcrText("Processing...");
    Tesseract.recognize(file, "eng").then(({ data: { text } }) => setOcrText(text));
  };

  // ===== Doc Scanner =====
  const handleDocScanner = async () => {
    if (!scannedFiles.length) return alert("No images scanned yet");
    const pdfDoc = await PDFDocument.create();
    for (let imgFile of scannedFiles) {
      const bytes = await imgFile.arrayBuffer();
      const img = imgFile.type.includes("png") ? await pdfDoc.embedPng(bytes) : await pdfDoc.embedJpg(bytes);
      const page = pdfDoc.addPage([img.width, img.height]);
      page.drawImage(img, { x: 0, y: 0, width: img.width, height: img.height });
    }
    const pdfBytes = await pdfDoc.save();
    const blob = new Blob([pdfBytes], { type: "application/pdf" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "scanned.pdf";
    link.click();
  };

  const addScannedFile = (e) => setScannedFiles([...scannedFiles, ...Array.from(e.target.files)]);

  return (
    <div className="app-container">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Tools</h2>
        <div className="tool-section">
          <h3>Background Remover</h3>
          <button onClick={handleRemoveBG}>Remove BG</button>
        </div>
        <div className="tool-section">
          <h3>Text Overlay</h3>
          <button onClick={() => addText()}>+ Add Text</button>
          <p style={{ fontSize: "12px", color: "#666", marginTop: "5px" }}>
            Or click anywhere on image to add text
          </p>
        </div>
        {/* Filters */}
        <div className="tool-section">
          <h3>Filters</h3>
          <select value={filter} onChange={(e) => setFilter(e.target.value)}>
            <option value="none">None</option>
            <option value="grayscale">Grayscale</option>
            <option value="sepia">Sepia</option>
            <option value="invert">Invert</option>
            <option value="blur">Blur</option>
            <option value="brightness">Brightness</option>
            <option value="contrast">Contrast</option>
            <option value="saturate">Saturate</option>
            <option value="hue-rotate">Hue Rotate</option>
          </select>
          <label>Intensity: {filterIntensity}%</label>
          <input type="range" min="0" max="100" value={filterIntensity} onChange={(e) => setFilterIntensity(e.target.value)} />
        </div>
        {/* Compression */}
        <div className="tool-section">
          <h3>Compression</h3>
          <input type="range" min="10" max="100" value={compression} onChange={(e) => setCompression(e.target.value)} />
          <span>{compression}%</span>
        </div>
        {/* Download */}
        <div className="tool-section">
          <h3>Download</h3>
          <button onClick={() => handleDownloadImage("jpeg")}>JPEG</button>
          <button onClick={() => handleDownloadImage("png")}>PNG</button>
          <button onClick={() => handleDownloadImage("pdf")}>PDF</button>
        </div>
        {/* OCR */}
        <div className="tool-section">
          <h3>OCR Extractor</h3>
          <button onClick={handleOCR}>Run OCR</button>
          {ocrText && (
            <div style={{ marginTop: "10px", maxHeight: "100px", overflow: "auto", border: "1px solid #ccc", padding: "5px" }}>
              <small>{ocrText}</small>
            </div>
          )}
        </div>
        {/* Doc Scanner */}
        <div className="tool-section">
          <h3>Doc Scanner</h3>
          <input type="file" multiple onChange={addScannedFile} />
          <button onClick={handleDocScanner}>Create PDF</button>
          {scannedFiles.length > 0 && <p>{scannedFiles.length} files selected</p>}
        </div>
      </div>

      {/* Editor */}
      <div className="editor">
        {!imageURL ? (
          <div
            className="upload-zone"
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onClick={() => document.getElementById("fileInput").click()}
          >
            <p>Drag & Drop file here or click to upload</p>
            <input id="fileInput" type="file" accept="image/*,application/pdf" onChange={handleFileChange} style={{ display: "none" }} />
          </div>
        ) : (
          <div className="editor-content" style={{ display: "flex", gap: "10px" }}>
            {/* Left: Image + Text */}
            <div style={{ flex: 3, position: "relative", minHeight: "500px", border: "1px solid #ccc", overflow: "hidden" }}>
              {file.type.startsWith("image/") ? (
                <div style={{ position: "relative", display: "inline-block" }}>
                  <img 
                    src={processedImage || imageURL} 
                    alt="Preview" 
                    style={{ maxWidth: "100%", display: "block", ...getFilterStyle() }}
                    onClick={handleImageClick}
                  />
                  {texts.map((t) => (
                    <Rnd
                      key={t.id}
                      size={{ width: t.width, height: t.height }}
                      position={{ x: t.x, y: t.y }}
                      onDragStop={(e, d) => {
                        updateText(t.id, "x", d.x);
                        updateText(t.id, "y", d.y);
                      }}
                      onResizeStop={(e, dir, ref, delta, pos) => {
                        updateText(t.id, "width", parseInt(ref.style.width));
                        updateText(t.id, "height", parseInt(ref.style.height));
                        updateText(t.id, "x", pos.x);
                        updateText(t.id, "y", pos.y);
                      }}
                      onClick={(e) => {
                        e.stopPropagation();
                        setSelectedTextId(t.id);
                        setShowTextTools(true);
                      }}
                      style={{ 
                        border: selectedTextId === t.id ? "2px dashed #007bff" : "none", 
                        position: "absolute", 
                        cursor: "move", 
                        backgroundColor: selectedTextId === t.id ? "rgba(255,255,255,0.2)" : "transparent",
                        borderRadius: 6,
                        zIndex: 10
                      }}
                    >
                      {selectedTextId === t.id && (
                        <span
                          onClick={(e) => {
                            e.stopPropagation();
                            deleteText(t.id);
                          }}
                          style={{
                            position: "absolute", top: -10, left: -10,
                            backgroundColor: "#ff4d4f", color: "#fff", borderRadius: "50%",
                            width: 20, height: 20, display: "flex", alignItems: "center", justifyContent: "center", cursor: "pointer", fontSize: 12, fontWeight: "bold", zIndex: 10,
                          }}
                        >
                          Ã—
                        </span>
                      )}
                      <div
                        contentEditable
                        suppressContentEditableWarning
                        style={{
                          width: "100%",
                          height: "100%",
                          color: t.color,
                          fontSize: t.fontSize,
                          outline: "none",
                          wordBreak: "break-word",
                          whiteSpace: "normal",
                          textAlign: "left",
                          display: "flex",
                          alignItems: "center",
                          padding: "4px",
                          fontFamily: "Arial",
                          lineHeight: "1.2",
                          overflow: "hidden"
                        }}
                        onInput={(e) => {
                          const newText = e.currentTarget.textContent;
                          updateText(t.id, "text", newText);
                          
                          // Auto-resize based on content
                          const textLength = newText.length;
                          const newWidth = Math.max(150, Math.min(400, textLength * 8 + 20));
                          const newHeight = Math.max(40, Math.ceil(textLength / 20) * 30 + 10);
                          updateText(t.id, "width", newWidth);
                          updateText(t.id, "height", newHeight);
                        }}
                      >
                        {t.text}
                      </div>
                    </Rnd>
                  ))}
                </div>
              ) : (
                <embed src={imageURL} type="application/pdf" width="100%" height="500px" />
              )}
            </div>

            {/* Right: Text Tools */}
            {showTextTools && selectedText && (
              <div style={{ flex: 1, border: "1px solid #ccc", padding: "15px", backgroundColor: "#f9f9f9" }}>
                <h3>Text Options</h3>
                
                <div style={{ marginBottom: "15px" }}>
                  <label style={{ display: "block", marginBottom: "5px", fontWeight: "bold" }}>
                    Text Content:
                  </label>
                  <input
                    type="text"
                    value={selectedText.text}
                    onChange={(e) => updateText(selectedTextId, "text", e.target.value)}
                    style={{ 
                      width: "100%", 
                      padding: "5px", 
                      border: "1px solid #ccc", 
                      borderRadius: "4px" 
                    }}
                  />
                </div>

                <div style={{ marginBottom: "15px" }}>
                  <label style={{ display: "block", marginBottom: "5px", fontWeight: "bold" }}>
                    Font Size: {selectedText.fontSize}px
                  </label>
                  <input
                    type="range"
                    min="12"
                    max="72"
                    value={selectedText.fontSize}
                    onChange={(e) => updateText(selectedTextId, "fontSize", parseInt(e.target.value))}
                    style={{ width: "100%" }}
                  />
                </div>

                <div style={{ marginBottom: "15px" }}>
                  <label style={{ display: "block", marginBottom: "5px", fontWeight: "bold" }}>
                    Text Color:
                  </label>
                  <input
                    type="color"
                    value={selectedText.color}
                    onChange={(e) => updateText(selectedTextId, "color", e.target.value)}
                    style={{ 
                      width: "100%", 
                      height: "40px", 
                      border: "1px solid #ccc", 
                      borderRadius: "4px",
                      cursor: "pointer"
                    }}
                  />
                </div>

                <div style={{ marginBottom: "15px" }}>
                  <label style={{ display: "block", marginBottom: "5px", fontWeight: "bold" }}>
                    Opacity: {Math.round(selectedText.opacity * 100)}%
                  </label>
                  <input
                    type="range"
                    min="0"
                    max="1"
                    step="0.1"
                    value={selectedText.opacity}
                    onChange={(e) => updateText(selectedTextId, "opacity", parseFloat(e.target.value))}
                    style={{ width: "100%" }}
                  />
                </div>

                <button
                  onClick={() => deleteText(selectedTextId)}
                  style={{
                    backgroundColor: "#ff4d4f",
                    color: "white",
                    border: "none",
                    padding: "8px 16px",
                    borderRadius: "4px",
                    cursor: "pointer",
                    width: "100%"
                  }}
                >
                  Delete Text
                </button>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
