import React from "react";

function ScannerConverter({ file }) {
  return (
    <div style={{ margin: "20px 0" }}>
      <h3>Scanner Converter</h3>
      <p>(Applying scanned effect visually)</p>
      {file && typeof file === "string" && (
        <img src={file} alt="Scanner effect" style={{ maxWidth: "300px", filter: "contrast(1.2) grayscale(0.2)" }} />
      )}
    </div>
  );
}

export default ScannerConverter;
