import React, { useState } from "react";

function CropTool({ file }) {
  const [manualCrop, setManualCrop] = useState(false);

  const handleAutoCrop = () => {
    alert("Auto crop applied (placeholder for now)");
  };

  return (
    <div style={{ margin: "20px 0" }}>
      <h3>Crop Tool</h3>
      <button onClick={handleAutoCrop}>Auto Crop</button>
      <button onClick={() => setManualCrop(!manualCrop)}>
        {manualCrop ? "Cancel Manual Crop" : "Manual Crop"}
      </button>
      {manualCrop && <p>Manual crop UI here (placeholder)</p>}
      {typeof file === "string" && <img src={file} alt="To crop" style={{ maxWidth: "300px", marginTop: "10px" }} />}
    </div>
  );
}

export default CropTool;
