import React, { useState } from "react";
import axios from "axios";
import { jsPDF } from "jspdf";
import { PDFDocument } from "pdf-lib";
import Tesseract from "tesseract.js";
import "./styles/App.css";

function App() {
  const [file, setFile] = useState(null);
  const [imageURL, setImageURL] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [ocrText, setOcrText] = useState("");
  const [activeTextBox, setActiveTextBox] = useState(null);
  const [placedTexts, setPlacedTexts] = useState([]);
  const [filter, setFilter] = useState("none");
  const [filterIntensity, setFilterIntensity] = useState(100);
  const [compression, setCompression] = useState(100);

  const [textTools, setTextTools] = useState({
    bold: false,
    italic: false,
    underline: false,
    uppercase: false,
    lowercase: false,
    color: "#000000",
    size: 20,
  });

  const handleFileChange = async (e) => {
    const uploadedFiles = Array.from(e.target.files);
    if (!uploadedFiles.length) return;
    if (uploadedFiles.length > 1) {
      await handleDocScanner(uploadedFiles);
      return;
    }
    const uploaded = uploadedFiles[0];
    setFile(uploaded);
    setImageURL(URL.createObjectURL(uploaded));
    setProcessedImage(null);
    setOcrText("");
  };

  const handleDrop = async (e) => {
    e.preventDefault();
    const droppedFiles = Array.from(e.dataTransfer.files);
    if (!droppedFiles.length) return;
    if (droppedFiles.length > 1) {
      await handleDocScanner(droppedFiles);
      return;
    }
    const dropped = droppedFiles[0];
    setFile(dropped);
    setImageURL(URL.createObjectURL(dropped));
    setProcessedImage(null);
    setOcrText("");
  };
  const handleDragOver = (e) => e.preventDefault();

  const handleOCR = async () => {
    if (!file || !file.type.startsWith("image/"))
      return alert("Upload an image for OCR");
    setOcrText("Processing...");
    Tesseract.recognize(file, "eng").then(({ data: { text } }) =>
      setOcrText(text)
    );
  };

  const handleAddText = () => {
    setActiveTextBox({
      text: "",
      x: 50,
      y: 50,
      bold: false,
      italic: false,
      underline: false,
      uppercase: false,
      lowercase: false,
      color: "#000000",
      size: 20,
    });
  };

  const handleTextChange = (e) => {
    setActiveTextBox({ ...activeTextBox, text: e.target.value });
  };

  const handlePlaceText = () => {
    setPlacedTexts([...placedTexts, activeTextBox]);
    setActiveTextBox(null);
  };

  const handleDeleteText = () => {
    setActiveTextBox(null);
  };

  const getFilterStyle = () => {
    const i = Number(filterIntensity);
    switch (filter) {
      case "grayscale":
        return { filter: `grayscale(${i}%)` };
      case "sepia":
        return { filter: `sepia(${i}%)` };
      case "invert":
        return { filter: `invert(${i}%)` };
      case "blur":
        return { filter: `blur(${i / 20}px)` };
      case "brightness":
        return { filter: `brightness(${i}%)` };
      case "contrast":
        return { filter: `contrast(${i}%)` };
      case "saturate":
        return { filter: `saturate(${i}%)` };
      case "hue-rotate":
        return { filter: `hue-rotate(${i * 3.6}deg)` };
      default:
        return {};
    }
  };

  const generateFinalImage = () => {
    return new Promise((resolve) => {
      const source = processedImage || imageURL;
      if (!source) return resolve(null);
      const img = new Image();
      img.crossOrigin = "anonymous";
      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");
        canvas.width = img.width;
        canvas.height = img.height;
        ctx.filter = getFilterStyle().filter || "none";
        ctx.drawImage(img, 0, 0);

        // Draw placed texts
        placedTexts.forEach((t) => {
          ctx.font = `${t.bold ? "bold" : ""} ${t.italic ? "italic" : ""} ${
            t.size
          }px Arial`;
          ctx.fillStyle = t.color;
          let text = t.text;
          if (t.uppercase) text = text.toUpperCase();
          if (t.lowercase) text = text.toLowerCase();
          ctx.fillText(text, t.x, t.y + t.size);
        });

        resolve(canvas.toDataURL("image/jpeg", Number(compression) / 100));
      };
      img.src = source;
    });
  };

  const handleDownloadImage = async (format) => {
    const finalDataURL = await generateFinalImage();
    if (!finalDataURL) return;
    if (format === "pdf") {
      const doc = new jsPDF();
      doc.addImage(finalDataURL, "JPEG", 10, 10, 190, 0);
      doc.save("docfixer.pdf");
    } else {
      const link = document.createElement("a");
      link.href = finalDataURL;
      link.download = `docfixer.${format}`;
      link.click();
    }
  };

  const handleDocScanner = async (files) => {
    const pdfDoc = await PDFDocument.create();
    for (let f of files) {
      const bytes = await f.arrayBuffer();
      const img = f.type.includes("png")
        ? await pdfDoc.embedPng(bytes)
        : await pdfDoc.embedJpg(bytes);
      const page = pdfDoc.addPage([img.width, img.height]);
      page.drawImage(img, { x: 0, y: 0, width: img.width, height: img.height });
    }
    const pdfBytes = await pdfDoc.save();
    const blob = new Blob([pdfBytes], { type: "application/pdf" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "scanned.pdf";
    link.click();
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <h2>Tools</h2>
        <div className="tool-section">
          <h3>Background Remover</h3>
          <button
            onClick={async () => {
              if (!file) return alert("Upload an image first");
              if (file.type.includes("pdf"))
                return alert("Works only with images");
              const formData = new FormData();
              formData.append("image_file", file);
              try {
                const res = await axios.post(
                  "https://api.remove.bg/v1.0/removebg",
                  formData,
                  {
                    headers: { "X-Api-Key": "VSNXBaMjvgBp6NjjRheLV3cW" },
                    responseType: "blob",
                  }
                );
                setProcessedImage(URL.createObjectURL(res.data));
              } catch (err) {
                alert("Remove.bg API error: " + err.message);
              }
            }}
          >
            Remove BG
          </button>
        </div>

        <div className="tool-section">
          <h3>Text Tools</h3>
          <button onClick={handleAddText}>Add Text</button>
          {activeTextBox && (
            <div style={{ marginTop: "10px" }}>
              <label>
                Bold:
                <input
                  type="checkbox"
                  checked={textTools.bold}
                  onChange={() =>
                    setTextTools({ ...textTools, bold: !textTools.bold })
                  }
                />
              </label>
              <label>
                Italic:
                <input
                  type="checkbox"
                  checked={textTools.italic}
                  onChange={() =>
                    setTextTools({ ...textTools, italic: !textTools.italic })
                  }
                />
              </label>
              <label>
                Underline:
                <input
                  type="checkbox"
                  checked={textTools.underline}
                  onChange={() =>
                    setTextTools({
                      ...textTools,
                      underline: !textTools.underline,
                    })
                  }
                />
              </label>
              <label>
                Uppercase:
                <input
                  type="checkbox"
                  checked={textTools.uppercase}
                  onChange={() =>
                    setTextTools({
                      ...textTools,
                      uppercase: !textTools.uppercase,
                    })
                  }
                />
              </label>
              <label>
                Lowercase:
                <input
                  type="checkbox"
                  checked={textTools.lowercase}
                  onChange={() =>
                    setTextTools({
                      ...textTools,
                      lowercase: !textTools.lowercase,
                    })
                  }
                />
              </label>
            </div>
          )}
        </div>

        <div className="tool-section">
          <h3>Filters</h3>
          <select value={filter} onChange={(e) => setFilter(e.target.value)}>
            <option value="none">None</option>
            <option value="grayscale">Grayscale</option>
            <option value="sepia">Sepia</option>
            <option value="invert">Invert</option>
            <option value="blur">Blur</option>
            <option value="brightness">Brightness</option>
            <option value="contrast">Contrast</option>
            <option value="saturate">Saturate</option>
            <option value="hue-rotate">Hue Rotate</option>
          </select>
          <label>Intensity: {filterIntensity}%</label>
          <input
            type="range"
            min="0"
            max="100"
            value={filterIntensity}
            onChange={(e) => setFilterIntensity(Number(e.target.value))}
          />
        </div>

        <div className="tool-section">
          <h3>Compression</h3>
          <input
            type="range"
            min="10"
            max="100"
            value={compression}
            onChange={(e) => setCompression(Number(e.target.value))}
          />
          <span>{compression}%</span>
        </div>

        <div className="tool-section">
          <h3>Download</h3>
          <button onClick={() => handleDownloadImage("jpeg")}>JPEG</button>
          <button onClick={() => handleDownloadImage("png")}>PNG</button>
          <button onClick={() => handleDownloadImage("pdf")}>PDF</button>
        </div>

        <div className="tool-section">
          <h3>OCR Extractor</h3>
          <button onClick={handleOCR}>Run OCR</button>
        </div>
      </div>

      <div className="editor">
        {!imageURL ? (
          <div
            className="upload-zone"
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onClick={() => document.getElementById("fileInput").click()}
          >
            <p>Drag & Drop file(s) here or click to upload</p>
            <input
              id="fileInput"
              type="file"
              accept="image/*"
              multiple
              onChange={handleFileChange}
              style={{ display: "none" }}
            />
          </div>
        ) : activeTextBox ? (
          <div style={{ display: "flex", gap: "10px" }}>
            <div style={{ flex: 3, position: "relative" }}>
              <img
                src={processedImage || imageURL}
                alt="Preview"
                style={{ maxWidth: "100%", ...getFilterStyle() }}
              />
              {/* Active Text Box */}
              <textarea
                value={activeTextBox.text}
                onChange={handleTextChange}
                style={{
                  position: "absolute",
                  top: activeTextBox.y,
                  left: activeTextBox.x,
                  fontWeight: textTools.bold ? "bold" : "normal",
                  fontStyle: textTools.italic ? "italic" : "normal",
                  textDecoration: textTools.underline ? "underline" : "none",
                  textTransform: textTools.uppercase
                    ? "uppercase"
                    : textTools.lowercase
                    ? "lowercase"
                    : "none",
                  color: textTools.color,
                  fontSize: textTools.size,
                  resize: "both",
                  overflow: "hidden",
                }}
              />
              {/* Tick / Cross */}
              <button
                style={{
                  position: "absolute",
                  top: activeTextBox.y - 25,
                  left: activeTextBox.x,
                }}
                onClick={handlePlaceText}
              >
                ✔
              </button>
              <button
                style={{
                  position: "absolute",
                  top: activeTextBox.y - 25,
                  left: activeTextBox.x + 30,
                }}
                onClick={handleDeleteText}
              >
                ✖
              </button>
            </div>
            {/* Right Text Tools */}
            <div style={{ flex: 1, border: "1px solid #ccc", padding: "10px" }}>
              <h3>Text Tools</h3>
              <label>
                Bold:
                <input
                  type="checkbox"
                  checked={textTools.bold}
                  onChange={() =>
                    setTextTools({ ...textTools, bold: !textTools.bold })
                  }
                />
              </label>
              <label>
                Italic:
                <input
                  type="checkbox"
                  checked={textTools.italic}
                  onChange={() =>
                    setTextTools({ ...textTools, italic: !textTools.italic })
                  }
                />
              </label>
              <label>
                Underline:
                <input
                  type="checkbox"
                  checked={textTools.underline}
                  onChange={() =>
                    setTextTools({
                      ...textTools,
                      underline: !textTools.underline,
                    })
                  }
                />
              </label>
              <label>
                Uppercase:
                <input
                  type="checkbox"
                  checked={textTools.uppercase}
                  onChange={() =>
                    setTextTools({
                      ...textTools,
                      uppercase: !textTools.uppercase,
                    })
                  }
                />
              </label>
              <label>
                Lowercase:
                <input
                  type="checkbox"
                  checked={textTools.lowercase}
                  onChange={() =>
                    setTextTools({
                      ...textTools,
                      lowercase: !textTools.lowercase,
                    })
                  }
                />
              </label>
            </div>
          </div>
        ) : ocrText ? (
          <div style={{ display: "flex", gap: "10px" }}>
            <div style={{ flex: 3, position: "relative" }}>
              <img
                src={processedImage || imageURL}
                alt="Preview"
                style={{ maxWidth: "100%", ...getFilterStyle() }}
              />
              {placedTexts.map((t, idx) => (
                <div
                  key={idx}
                  style={{
                    position: "absolute",
                    left: t.x,
                    top: t.y,
                    fontSize: t.size,
                    fontWeight: t.bold ? "bold" : "normal",
                    fontStyle: t.italic ? "italic" : "normal",
                    textDecoration: t.underline ? "underline" : "none",
                    color: t.color,
                    textTransform: t.uppercase
                      ? "uppercase"
                      : t.lowercase
                      ? "lowercase"
                      : "none",
                  }}
                >
                  {t.text}
                </div>
              ))}
            </div>
            <div style={{ flex: 1, border: "1px solid #ccc", padding: "10px" }}>
              <h3>OCR Output (Editable)</h3>
              <textarea
                rows="15"
                value={ocrText}
                onChange={(e) => setOcrText(e.target.value)}
                style={{ width: "100%", resize: "vertical" }}
              />
            </div>
          </div>
        ) : (
          <img
            src={processedImage || imageURL}
            alt="Preview"
            style={{ maxWidth: "100%", ...getFilterStyle() }}
          />
        )}
      </div>
    </div>
  );
}

export default App;
