import React, { useState } from "react";
import axios from "axios";
import { jsPDF } from "jspdf";
import { PDFDocument } from "pdf-lib";
import Tesseract from "tesseract.js";
import { Rnd } from "react-rnd";
import "./styles/App.css";

function App() {
  const [file, setFile] = useState(null);
  const [imageURL, setImageURL] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [filter, setFilter] = useState("none");
  const [filterIntensity, setFilterIntensity] = useState(100);
  const [compression, setCompression] = useState(100);
  const [ocrText, setOcrText] = useState("");
  const [textBoxes, setTextBoxes] = useState([]); // editable text boxes
  const [placedTexts, setPlacedTexts] = useState([]); // finalized text
  const [activeTextBox, setActiveTextBox] = useState(null); // currently editing text box

  // File upload
  const handleFileChange = async (e) => {
    const uploadedFiles = Array.from(e.target.files);
    if (!uploadedFiles.length) return;

    if (uploadedFiles.length > 1) {
      await handleDocScanner(uploadedFiles);
      return;
    }

    const uploaded = uploadedFiles[0];
    setFile(uploaded);
    setImageURL(URL.createObjectURL(uploaded));
    setProcessedImage(null);
    setOcrText("");
    setTextBoxes([]);
    setPlacedTexts([]);
  };

  const handleDrop = async (e) => {
    e.preventDefault();
    const droppedFiles = Array.from(e.dataTransfer.files);
    if (!droppedFiles.length) return;

    if (droppedFiles.length > 1) {
      await handleDocScanner(droppedFiles);
      return;
    }

    const dropped = droppedFiles[0];
    setFile(dropped);
    setImageURL(URL.createObjectURL(dropped));
    setProcessedImage(null);
    setOcrText("");
    setTextBoxes([]);
    setPlacedTexts([]);
  };
  const handleDragOver = (e) => e.preventDefault();

  // Background remover
  const handleRemoveBG = async () => {
    if (!file) return alert("Upload an image first");
    if (file.type.includes("pdf")) return alert("Works only with images");
    const formData = new FormData();
    formData.append("image_file", file);
    try {
      const res = await axios.post(
        "https://api.remove.bg/v1.0/removebg",
        formData,
        {
          headers: { "X-Api-Key": "VSNXBaMjvgBp6NjjRheLV3cW" },
          responseType: "blob",
        }
      );
      setProcessedImage(URL.createObjectURL(res.data));
    } catch (err) {
      alert("Remove.bg API error: " + err.message);
    }
  };

  // Filter
  const getFilterStyle = () => {
    const i = Number(filterIntensity);
    switch (filter) {
      case "grayscale":
        return { filter: `grayscale(${i}%)` };
      case "sepia":
        return { filter: `sepia(${i}%)` };
      case "invert":
        return { filter: `invert(${i}%)` };
      case "blur":
        return { filter: `blur(${i / 20}px)` };
      case "brightness":
        return { filter: `brightness(${i}%)` };
      case "contrast":
        return { filter: `contrast(${i}%)` };
      case "saturate":
        return { filter: `saturate(${i}%)` };
      case "hue-rotate":
        return { filter: `hue-rotate(${i * 3.6}deg)` };
      default:
        return {};
    }
  };

  // Generate image (with filters + compression + placed texts)
  const generateFinalImage = () => {
    return new Promise((resolve) => {
      const source = processedImage || imageURL;
      if (!source) return resolve(null);
      const img = new Image();
      img.crossOrigin = "anonymous";
      img.onload = () => {
        const canvas = document.createElement("canvas");
        canvas.width = img.width;
        canvas.height = img.height;
        const ctx = canvas.getContext("2d");
        ctx.filter = getFilterStyle().filter || "none";
        ctx.drawImage(img, 0, 0);

        // draw placed texts
        placedTexts.forEach((t) => {
          ctx.font = `${t.bold ? "bold" : ""} ${t.italic ? "italic" : ""} ${t.size}px Arial`;
          ctx.fillStyle = t.color;
          ctx.textBaseline = "top";
          const text = t.uppercase
            ? t.text.toUpperCase()
            : t.lowercase
            ? t.text.toLowerCase()
            : t.text;
          ctx.fillText(text, t.x, t.y);
        });

        resolve(canvas.toDataURL("image/jpeg", Number(compression) / 100));
      };
      img.src = source;
    });
  };

  // Download
  const handleDownloadImage = async (format) => {
    const finalDataURL = await generateFinalImage();
    if (!finalDataURL) return;
    if (format === "pdf") {
      const doc = new jsPDF();
      doc.addImage(finalDataURL, "JPEG", 10, 10, 190, 0);
      doc.save("docfixer.pdf");
    } else {
      const link = document.createElement("a");
      link.href = finalDataURL;
      link.download = `docfixer.${format}`;
      link.click();
    }
  };

  // OCR
  const handleOCR = async () => {
    if (!file || !file.type.startsWith("image/"))
      return alert("Upload an image for OCR");
    setOcrText("Processing...");
    Tesseract.recognize(file, "eng").then(({ data: { text } }) =>
      setOcrText(text)
    );
  };

  // Doc scanner → auto PDF
  const handleDocScanner = async (files) => {
    const pdfDoc = await PDFDocument.create();
    for (let f of files) {
      const bytes = await f.arrayBuffer();
      const img = f.type.includes("png")
        ? await pdfDoc.embedPng(bytes)
        : await pdfDoc.embedJpg(bytes);
      const page = pdfDoc.addPage([img.width, img.height]);
      page.drawImage(img, { x: 0, y: 0, width: img.width, height: img.height });
    }
    const pdfBytes = await pdfDoc.save();
    const blob = new Blob([pdfBytes], { type: "application/pdf" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "scanned.pdf";
    link.click();
  };

  // Add text box
  const addTextBox = () => {
    const id = Date.now();
    const newBox = {
      id,
      x: 50,
      y: 50,
      width: 150,
      height: 50,
      text: "",
      bold: false,
      italic: false,
      underline: false,
      uppercase: false,
      lowercase: false,
      size: 20,
      color: "#000000",
    };
    setTextBoxes([...textBoxes, newBox]);
    setActiveTextBox(id);
  };

  const handleTextInput = (id, value) => {
    setTextBoxes(textBoxes.map((t) => (t.id === id ? { ...t, text: value } : t)));
  };

  const updateTextBox = (id, props) => {
    setTextBoxes(textBoxes.map((t) => (t.id === id ? { ...t, ...props } : t)));
  };

  const finalizeTextBox = (id) => {
    const box = textBoxes.find((t) => t.id === id);
    if (box && box.text.trim() !== "") {
      setPlacedTexts([...placedTexts, box]);
    }
    setTextBoxes(textBoxes.filter((t) => t.id !== id));
    setActiveTextBox(null);
  };

  const deleteTextBox = (id) => {
    setTextBoxes(textBoxes.filter((t) => t.id !== id));
    if (activeTextBox === id) setActiveTextBox(null);
  };

  const toggleStyle = (style) => {
    setTextBoxes(
      textBoxes.map((t) =>
        t.id === activeTextBox ? { ...t, [style]: !t[style] } : t
      )
    );
  };

  const changeSize = (size) => {
    setTextBoxes(
      textBoxes.map((t) =>
        t.id === activeTextBox ? { ...t, size: Number(size) } : t
      )
    );
  };

  const changeColor = (color) => {
    setTextBoxes(
      textBoxes.map((t) =>
        t.id === activeTextBox ? { ...t, color } : t
      )
    );
  };

  return (
    <div className="app-container">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Tools</h2>

        <div className="tool-section">
          <h3>Background Remover</h3>
          <button onClick={handleRemoveBG}>Remove BG</button>
        </div>

        <div className="tool-section">
          <h3>Filters</h3>
          <select value={filter} onChange={(e) => setFilter(e.target.value)}>
            <option value="none">None</option>
            <option value="grayscale">Grayscale</option>
            <option value="sepia">Sepia</option>
            <option value="invert">Invert</option>
            <option value="blur">Blur</option>
            <option value="brightness">Brightness</option>
            <option value="contrast">Contrast</option>
            <option value="saturate">Saturate</option>
            <option value="hue-rotate">Hue Rotate</option>
          </select>
          <label>Intensity: {filterIntensity}%</label>
          <input
            type="range"
            min="0"
            max="100"
            value={filterIntensity}
            onChange={(e) => setFilterIntensity(Number(e.target.value))}
          />
        </div>

        <div className="tool-section">
          <h3>Compression</h3>
          <input
            type="range"
            min="10"
            max="100"
            value={compression}
            onChange={(e) => setCompression(Number(e.target.value))}
          />
          <span>{compression}%</span>
        </div>

        <div className="tool-section">
          <h3>Add Text</h3>
          <button onClick={addTextBox}>Add Text</button>
        </div>

        <div className="tool-section">
          <h3>Download</h3>
          <button onClick={() => handleDownloadImage("jpeg")}>JPEG</button>
          <button onClick={() => handleDownloadImage("png")}>PNG</button>
          <button onClick={() => handleDownloadImage("pdf")}>PDF</button>
        </div>

        <div className="tool-section">
          <h3>OCR Extractor</h3>
          <button onClick={handleOCR}>Run OCR</button>
        </div>
      </div>

      {/* Editor */}
      <div className="editor">
        {activeTextBox ? (
          <div style={{ display: "flex", gap: "10px" }}>
            {/* Left: Image preview */}
            <div style={{ flex: 3, position: "relative" }}>
              {!imageURL ? (
                <div
                  className="upload-zone"
                  onDrop={handleDrop}
                  onDragOver={handleDragOver}
                  onClick={() => document.getElementById("fileInput").click()}
                >
                  <p>Drag & Drop file(s) here or click to upload</p>
                  <input
                    id="fileInput"
                    type="file"
                    accept="image/*"
                    multiple
                    onChange={handleFileChange}
                    style={{ display: "none" }}
                  />
                </div>
              ) : (
                <img
                  src={processedImage || imageURL}
                  alt="Preview"
                  style={{ maxWidth: "100%", ...getFilterStyle() }}
                />
              )}

              {/* Text boxes */}
              {textBoxes.map((box) => (
                <Rnd
                  key={box.id}
                  size={{ width: box.width, height: box.height }}
                  position={{ x: box.x, y: box.y }}
                  onDragStop={(e, d) => updateTextBox(box.id, { x: d.x, y: d.y })}
                  onResizeStop={(e, direction, ref, delta, position) =>
                    updateTextBox(box.id, {
                      width: parseInt(ref.style.width),
                      height: parseInt(ref.style.height),
                      ...position,
                    })
                  }
                  bounds="parent"
                >
                  <div
                    style={{
                      border: "1px dashed #333",
                      padding: "4px",
                      minHeight: "20px",
                      width: "100%",
                      height: "100%",
                      fontSize: box.size,
                      fontWeight: box.bold ? "bold" : "normal",
                      fontStyle: box.italic ? "italic" : "normal",
                      textDecoration: box.underline ? "underline" : "none",
                      color: box.color,
                      textTransform: box.uppercase ? "uppercase" : box.lowercase ? "lowercase" : "none",
                    }}
                  >
                    <textarea
                      value={box.text}
                      onChange={(e) => handleTextInput(box.id, e.target.value)}
                      style={{
                        width: "100%",
                        height: "100%",
                        resize: "none",
                        background: "transparent",
                        border: "none",
                        outline: "none",
                        color: box.color,
                        fontSize: box.size,
                        fontWeight: box.bold ? "bold" : "normal",
                        fontStyle: box.italic ? "italic" : "normal",
                        textDecoration: box.underline ? "underline" : "none",
                        textTransform: box.uppercase ? "uppercase" : box.lowercase ? "lowercase" : "none",
                      }}
                    />
                    <div style={{ display: "flex", justifyContent: "flex-end", gap: "5px" }}>
                      <button onClick={() => finalizeTextBox(box.id)}>✅</button>
                      <button onClick={() => deleteTextBox(box.id)}>❌</button>
                    </div>
                  </div>
                </Rnd>
              ))}

              {/* Placed texts */}
              {placedTexts.map((t, idx) => (
                <div
                  key={idx}
                  style={{
                    position: "absolute",
                    left: t.x,
                    top: t.y,
                    fontSize: t.size,
                    fontWeight: t.bold ? "bold" : "normal",
                    fontStyle: t.italic ? "italic" : "normal",
                    textDecoration: t.underline ? "underline" : "none",
                    color: t.color,
                    textTransform: t.uppercase ? "uppercase" : t.lowercase ? "lowercase" : "none",
                  }}
                >
                  {t.text}
                </div>
              ))}
            </div>

            {/* Right: Text editing panel */}
            <div style={{ flex: 1, border: "1px solid #ccc", padding: "10px" }}>
              <h3>Text Tools</h3>
              <button onClick={() => toggleStyle("bold")}>Bold</button>
              <button onClick={() => toggleStyle("italic")}>Italic</button>
              <button onClick={() => toggleStyle("underline")}>Underline</button>
              <button onClick={() => toggleStyle("uppercase")}>Uppercase</button>
              <button onClick={() => toggleStyle("lowercase")}>Lowercase</button>
              <div>
                <label>Font Size:</label>
                <input
                  type="number"
                  value={textBoxes.find((t) => t.id === activeTextBox)?.size || 20}
                  onChange={(e) => changeSize(e.target.value)}
                  min="10"
                  max="100"
                />
              </div>
              <div>
                <label>Color:</label>
                <input
                  type="color"
                  value={textBoxes.find((t) => t.id === activeTextBox)?.color || "#000000"}
                  onChange={(e) => changeColor(e.target.value)}
                />
              </div>
            </div>
          </div>
        ) : (
          // Normal view without text tool split
          <div style={{ position: "relative" }}>
            {!imageURL ? (
              <div
                className="upload-zone"
                onDrop={handleDrop}
                onDragOver={handleDragOver}
                onClick={() => document.getElementById("fileInput").click()}
              >
                <p>Drag & Drop file(s) here or click to upload</p>
                <input
                  id="fileInput"
                  type="file"
                  accept="image/*"
                  multiple
                  onChange={handleFileChange}
                  style={{ display: "none" }}
                />
              </div>
            ) : (
              <img
                src={processedImage || imageURL}
                alt="Preview"
                style={{ maxWidth: "100%", ...getFilterStyle() }}
              />
            )}

            {/* Placed texts */}
            {placedTexts.map((t, idx) => (
              <div
                key={idx}
                style={{
                  position: "absolute",
                  left: t.x,
                  top: t.y,
                  fontSize: t.size,
                  fontWeight: t.bold ? "bold" : "normal",
                  fontStyle: t.italic ? "italic" : "normal",
                  textDecoration: t.underline ? "underline" : "none",
                  color: t.color,
                  textTransform: t.uppercase ? "uppercase" : t.lowercase ? "lowercase" : "none",
                }}
              >
                {t.text}
              </div>
            ))}
          </div>
        )}

        {ocrText && (
          <div style={{ marginTop: 20 }}>
            <h3>OCR Extracted Text</h3>
            <textarea
              rows="8"
              value={ocrText}
              readOnly
              style={{ width: "100%" }}
            />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
