import React, { useState, useRef, useEffect } from "react";
import { fabric } from "fabric";

function DocEditor({ file, onUpdate }) {
  const canvasRef = useRef(null);
  const [text, setText] = useState("");

  useEffect(() => {
    const canvas = new fabric.Canvas("canvas", { width: 500, height: 600 });
    canvasRef.current = canvas;

    fabric.Image.fromURL(file, (img) => {
      canvas.clear();
      canvas.setWidth(img.width > 500 ? 500 : img.width);
      canvas.setHeight(img.height > 600 ? 600 : img.height);
      canvas.add(img);
      canvas.sendToBack(img);
    });
  }, [file]);

  const handleAddText = () => {
    if (text.trim() === "") return;
    const canvas = canvasRef.current;
    const textObj = new fabric.Text(text, { left: 50, top: 50, fill: "red", fontSize: 20 });
    canvas.add(textObj);
    canvas.setActiveObject(textObj);
  };

  const handleUpdate = () => {
    const canvas = canvasRef.current;
    const dataUrl = canvas.toDataURL({ format: "png" });
    onUpdate(dataUrl);
  };

  return (
    <div style={{ margin: "20px 0" }}>
      <h3>Document Editor</h3>
      <input type="text" placeholder="Enter text" value={text} onChange={(e) => setText(e.target.value)} />
      <button onClick={handleAddText}>Add Text</button>
      <button onClick={handleUpdate}>Apply Edits</button>
      <canvas id="canvas" style={{ border: "1px solid #ccc", marginTop: "10px" }} />
    </div>
  );
}

export default DocEditor;
