import React, { useState } from "react";
import axios from "axios";

function BackgroundRemover({ file }) {
  const [result, setResult] = useState("");

  const handleRemoveBg = async () => {
    const res = await axios.post("http://localhost:5000/api/remove-bg", { imagePath: file.replace("http://localhost:5000/", "uploads/") });
    setResult(res.data.url);
  };

  return (
    <div style={{ margin: "20px 0" }}>
      <h3>Background Remover</h3>
      <button onClick={handleRemoveBg}>Remove Background</button>
      {result && <img src={result} alt="No background" style={{ maxWidth: "300px", marginTop: "10px" }} />}
    </div>
  );
}

export default BackgroundRemover;
