import React, { useState } from "react";
import axios from "axios";
import { Rnd } from "react-rnd";
import { HexColorPicker } from "react-colorful";
import "./styles/App.css";

function App() {
  const [file, setFile] = useState(null);
  const [imageURL, setImageURL] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [filter, setFilter] = useState("none");
  const [compression, setCompression] = useState(100);

  const [texts, setTexts] = useState([]);
  const [selectedTextId, setSelectedTextId] = useState(null);
  const [showColorPicker, setShowColorPicker] = useState(false);

  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    if (!uploadedFile) return;
    setFile(uploadedFile);
    setImageURL(URL.createObjectURL(uploadedFile));
    setProcessedImage(null);
    setTexts([]);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile) {
      setFile(droppedFile);
      setImageURL(URL.createObjectURL(droppedFile));
      setProcessedImage(null);
      setTexts([]);
    }
  };

  const handleDragOver = (e) => e.preventDefault();

  const handleRemoveBG = async () => {
    if (!file) return alert("Upload an image first");
    if (file.type.includes("pdf")) return alert("Remove.bg works only with images");

    const formData = new FormData();
    formData.append("image_file", file);
    try {
      const res = await axios.post("https://api.remove.bg/v1.0/removebg", formData, {
        headers: {
          "X-Api-Key": "VSNXBaMjvgBp6NjjRheLV3cW",
          "Content-Type": "multipart/form-data",
        },
        responseType: "blob",
      });
      const url = URL.createObjectURL(res.data);
      setProcessedImage(url);
    } catch (err) {
      alert("Remove.bg API error: " + err.message);
    }
  };

  const getFilterStyle = () => {
    switch (filter) {
      case "grayscale":
        return { filter: "grayscale(100%)" };
      case "sepia":
        return { filter: "sepia(100%)" };
      case "invert":
        return { filter: "invert(100%)" };
      default:
        return {};
    }
  };

  const addText = () => {
    const newText = {
      id: Date.now(),
      text: "New Text",
      color: "#ff0000",
      fontSize: 24,
      x: 50,
      y: 50,
      width: 200,
      height: 50,
      rotation: 0,
      opacity: 1,
    };
    setTexts([...texts, newText]);
    setSelectedTextId(newText.id);
  };

  const updateText = (id, key, value) => {
    setTexts(texts.map((t) => (t.id === id ? { ...t, [key]: value } : t)));
  };

  const deleteText = (id) => {
    setTexts(texts.filter((t) => t.id !== id));
    if (selectedTextId === id) setSelectedTextId(null);
  };

  const generateFinalImage = () => {
    return new Promise((resolve) => {
      const source = processedImage || imageURL;
      if (!source) return resolve(null);

      const img = new Image();
      img.crossOrigin = "anonymous";
      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");
        canvas.width = img.width;
        canvas.height = img.height;

        ctx.filter =
          filter === "grayscale"
            ? "grayscale(100%)"
            : filter === "sepia"
            ? "sepia(100%)"
            : filter === "invert"
            ? "invert(100%)"
            : "none";

        ctx.drawImage(img, 0, 0);

        texts.forEach((t) => {
          ctx.save();
          ctx.translate(t.x + t.width / 2, t.y + t.height / 2);
          ctx.rotate((t.rotation * Math.PI) / 180);
          ctx.globalAlpha = t.opacity;
          ctx.font = `${t.fontSize}px Arial`;
          ctx.fillStyle = t.color;
          ctx.textAlign = "center";
          ctx.textBaseline = "middle";
          ctx.fillText(t.text, 0, 0);
          ctx.restore();
        });

        resolve(canvas.toDataURL("image/jpeg", compression / 100));
      };
      img.src = source;
    });
  };

  const handleDownload = async (format) => {
    if (file?.type.includes("pdf")) {
      const link = document.createElement("a");
      link.href = imageURL;
      link.download = "docfixer.pdf";
      link.click();
      return;
    }
    const finalDataURL = await generateFinalImage();
    if (!finalDataURL) return;
    const link = document.createElement("a");
    link.href = finalDataURL;
    link.download = `docfixer.${format}`;
    link.click();
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <h2>Tools</h2>
        <div className="tool-section">
          <h3>Background Remover</h3>
          <button onClick={handleRemoveBG}>Remove BG</button>
        </div>

        <div className="tool-section">
          <h3>Text Overlay</h3>
          <button onClick={addText}>+ Add Text</button>
        </div>

        <div className="tool-section">
          <h3>Filters</h3>
          <select value={filter} onChange={(e) => setFilter(e.target.value)}>
            <option value="none">None</option>
            <option value="grayscale">Grayscale</option>
            <option value="sepia">Sepia</option>
            <option value="invert">Invert</option>
          </select>
        </div>

        <div className="tool-section">
          <h3>Compression</h3>
          <input
            type="range"
            min="10"
            max="100"
            value={compression}
            onChange={(e) => setCompression(e.target.value)}
          />
          <span>{compression}%</span>
        </div>

        <div className="tool-section">
          <h3>Download</h3>
          <button onClick={() => handleDownload("jpeg")}>JPEG</button>
          <button onClick={() => handleDownload("png")}>PNG</button>
          <button onClick={() => handleDownload("pdf")}>PDF</button>
        </div>
      </div>

      <div className="editor" onDrop={handleDrop} onDragOver={handleDragOver}>
        {!imageURL && (
          <div className="upload-zone">
            <p>Drag & Drop file here or click to upload</p>
            <input
              type="file"
              accept="image/*,application/pdf"
              onChange={handleFileChange}
            />
          </div>
        )}

        {imageURL && (
          <div className="preview-area" style={{ position: "relative" }}>
            {file?.type.startsWith("image/") && (
              <div style={{ position: "relative" }}>
                <img
                  src={processedImage || imageURL}
                  alt="Preview"
                  style={{ maxWidth: "100%", ...getFilterStyle() }}
                />

                {texts.map((t) => (
                  <Rnd
                    key={t.id}
                    size={{ width: t.width, height: t.height }}
                    position={{ x: t.x, y: t.y }}
                    onDragStop={(e, d) => {
                      updateText(t.id, "x", d.x);
                      updateText(t.id, "y", d.y);
                    }}
                    onResizeStop={(e, direction, ref, delta, position) => {
                      updateText(t.id, "width", parseInt(ref.style.width));
                      updateText(t.id, "height", parseInt(ref.style.height));
                      updateText(t.id, "x", position.x);
                      updateText(t.id, "y", position.y);
                    }}
                    onClick={() => setSelectedTextId(t.id)}
                    style={{
                      border: selectedTextId === t.id ? "2px dashed #007bff" : "none",
                      padding: "2px",
                      cursor: "move",
                      backgroundColor: "rgba(255,255,255,0.2)",
                      overflow: "hidden",
                      transform: `rotate(${t.rotation}deg)`,
                      opacity: t.opacity,
                    }}
                  >
                    <div
                      contentEditable
                      suppressContentEditableWarning
                      style={{
                        width: "100%",
                        height: "100%",
                        textAlign: "center",
                        cursor: "text",
                        color: t.color,
                        fontSize: t.fontSize,
                      }}
                      onInput={(e) =>
                        updateText(t.id, "text", e.currentTarget.textContent)
                      }
                    >
                      {t.text}
                    </div>

                    {/* Floating Toolbar */}
                    {selectedTextId === t.id && (
                      <div
                        className="floating-toolbar"
                        style={{
                          position: "absolute",
                          top: -80,
                          left: 0,
                          background: "#fff",
                          padding: "5px",
                          borderRadius: "8px",
                          boxShadow: "0 2px 6px rgba(0,0,0,0.2)",
                          zIndex: 10,
                          display: "flex",
                          flexDirection: "column",
                          gap: "5px",
                        }}
                      >
                        <input
                          type="text"
                          value={t.text}
                          onChange={(e) => updateText(t.id, "text", e.target.value)}
                        />
                        <input
                          type="number"
                          min="10"
                          max="200"
                          value={t.fontSize}
                          onChange={(e) => updateText(t.id, "fontSize", parseInt(e.target.value))}
                        />
                        <button onClick={() => setShowColorPicker(!showColorPicker)}>Color</button>
                        {showColorPicker && (
                          <HexColorPicker
                            color={t.color}
                            onChange={(color) => updateText(t.id, "color", color)}
                          />
                        )}
                        <label>
                          Rotation
                          <input
                            type="range"
                            min="0"
                            max="360"
                            value={t.rotation}
                            onChange={(e) => updateText(t.id, "rotation", parseInt(e.target.value))}
                          />
                        </label>
                        <label>
                          Opacity
                          <input
                            type="range"
                            min="0"
                            max="1"
                            step="0.05"
                            value={t.opacity}
                            onChange={(e) => updateText(t.id, "opacity", parseFloat(e.target.value))}
                          />
                        </label>
                        <button onClick={() => deleteText(t.id)}>Delete</button>
                      </div>
                    )}
                  </Rnd>
                ))}
              </div>
            )}

            {file?.type.includes("pdf") && (
              <embed src={imageURL} type="application/pdf" width="100%" height="600px" />
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
