import React, { useState } from "react";
import axios from "axios";

function BackgroundRemover({ file, setFile }) {
  const [loading, setLoading] = useState(false);

  const handleRemoveBg = async () => {
    try {
      setLoading(true);
      const res = await axios.post("http://localhost:5000/api/remove-bg", {
        imagePath: file.replace("http://localhost:5000/", "")
      });
      setFile(`http://localhost:5000/${res.data.url}`);
    } catch (err) {
      console.error(err);
      alert("Failed to remove background");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ margin: "20px 0" }}>
      <h3>Background Remover</h3>
      <button onClick={handleRemoveBg} disabled={loading}>
        {loading ? "Processing..." : "Remove Background"}
      </button>
      {file && <img src={file} alt="Edited" style={{ maxWidth: "300px", marginTop: "10px" }} />}
    </div>
  );
}

export default BackgroundRemover;
