import React from "react";

function Filters({ file, onUpdate, filter }) {
  const filters = {
    none: "",
    grayscale: "grayscale(100%)",
    sepia: "sepia(100%)",
    invert: "invert(100%)"
  };

  return (
    <div style={{ margin: "20px 0" }}>
      <h3>Filters</h3>
      <select value={filter} onChange={(e) => onUpdate(filters[e.target.value])}>
        {Object.keys(filters).map((f) => <option key={f} value={f}>{f}</option>)}
      </select>
      {file && <img src={file} style={{ maxWidth: "300px", marginTop: "10px", filter: filter }} />}
    </div>
  );
}

export default Filters;
