import React, { useState } from "react";
import axios from "axios";
import { jsPDF } from "jspdf";
import { PDFDocument } from "pdf-lib";
import Tesseract from "tesseract.js";
import TextEditor from "./components/TextEditor";
import "./styles/App.css";

function App() {
  const [file, setFile] = useState(null);
  const [imageURL, setImageURL] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [filter, setFilter] = useState("none");
  const [filterIntensity, setFilterIntensity] = useState(100);
  const [compression, setCompression] = useState(100);
  const [ocrText, setOcrText] = useState("");
  const [textBoxes, setTextBoxes] = useState([]); // store text boxes

  // File upload
  const handleFileChange = async (e) => {
    const uploadedFiles = Array.from(e.target.files);
    if (!uploadedFiles.length) return;

    if (uploadedFiles.length > 1) {
      await handleDocScanner(uploadedFiles);
      return;
    }

    const uploaded = uploadedFiles[0];
    setFile(uploaded);
    setImageURL(URL.createObjectURL(uploaded));
    setProcessedImage(null);
    setOcrText("");
  };

  const handleDrop = async (e) => {
    e.preventDefault();
    const droppedFiles = Array.from(e.dataTransfer.files);
    if (!droppedFiles.length) return;

    if (droppedFiles.length > 1) {
      await handleDocScanner(droppedFiles);
      return;
    }

    const dropped = droppedFiles[0];
    setFile(dropped);
    setImageURL(URL.createObjectURL(dropped));
    setProcessedImage(null);
    setOcrText("");
  };
  const handleDragOver = (e) => e.preventDefault();

  // Background remover
  const handleRemoveBG = async () => {
    if (!file) return alert("Upload an image first");
    if (file.type.includes("pdf")) return alert("Works only with images");
    const formData = new FormData();
    formData.append("image_file", file);
    try {
      const res = await axios.post(
        "https://api.remove.bg/v1.0/removebg",
        formData,
        {
          headers: { "X-Api-Key": "VSNXBaMjvgBp6NjjRheLV3cW" },
          responseType: "blob",
        }
      );
      setProcessedImage(URL.createObjectURL(res.data));
    } catch (err) {
      alert("Remove.bg API error: " + err.message);
    }
  };

  // Filters
  const getFilterStyle = () => {
    const i = Number(filterIntensity);
    switch (filter) {
      case "grayscale":
        return { filter: `grayscale(${i}%)` };
      case "sepia":
        return { filter: `sepia(${i}%)` };
      case "invert":
        return { filter: `invert(${i}%)` };
      case "blur":
        return { filter: `blur(${i / 20}px)` };
      case "brightness":
        return { filter: `brightness(${i}%)` };
      case "contrast":
        return { filter: `contrast(${i}%)` };
      case "saturate":
        return { filter: `saturate(${i}%)` };
      case "hue-rotate":
        return { filter: `hue-rotate(${i * 3.6}deg)` };
      default:
        return {};
    }
  };

  // Generate final image including text boxes
  const generateFinalImage = () => {
    return new Promise((resolve) => {
      const source = processedImage || imageURL;
      if (!source) return resolve(null);
      const img = new Image();
      img.crossOrigin = "anonymous";
      img.onload = () => {
        const canvas = document.createElement("canvas");
        canvas.width = img.width;
        canvas.height = img.height;
        const ctx = canvas.getContext("2d");

        // draw image with filter
        ctx.filter = getFilterStyle().filter || "none";
        ctx.drawImage(img, 0, 0);

        // draw text boxes
        textBoxes.forEach((tb) => {
          ctx.font = `${tb.bold ? "bold " : ""}${tb.italic ? "italic " : ""}${
            tb.fontSize
          }px ${tb.fontFamily}`;
          ctx.fillStyle = tb.color;
          let text = tb.upper
            ? tb.text.toUpperCase()
            : tb.lower
            ? tb.text.toLowerCase()
            : tb.text;
          if (tb.underline) {
            const width = ctx.measureText(text).width;
            ctx.fillText(text, tb.x, tb.y + tb.fontSize);
            ctx.beginPath();
            ctx.moveTo(tb.x, tb.y + tb.fontSize + 2);
            ctx.lineTo(tb.x + width, tb.y + tb.fontSize + 2);
            ctx.strokeStyle = tb.color;
            ctx.lineWidth = 1;
            ctx.stroke();
          } else {
            ctx.fillText(text, tb.x, tb.y + tb.fontSize);
          }
        });

        resolve(canvas.toDataURL("image/jpeg", compression / 100));
      };
      img.src = source;
    });
  };

  // Download image or PDF
  const handleDownloadImage = async (format) => {
    const finalDataURL = await generateFinalImage();
    if (!finalDataURL) return;
    if (format === "pdf") {
      const doc = new jsPDF();
      doc.addImage(finalDataURL, "JPEG", 10, 10, 190, 0);
      doc.save("docfixer.pdf");
    } else {
      const link = document.createElement("a");
      link.href = finalDataURL;
      link.download = `docfixer.${format}`;
      link.click();
    }
  };

  // OCR
  const handleOCR = async () => {
    if (!file || !file.type.startsWith("image/"))
      return alert("Upload an image for OCR");
    setOcrText("Processing...");
    Tesseract.recognize(file, "eng").then(({ data: { text } }) =>
      setOcrText(text)
    );
  };

  // Doc scanner → auto PDF
  const handleDocScanner = async (files) => {
    const pdfDoc = await PDFDocument.create();
    for (let f of files) {
      const bytes = await f.arrayBuffer();
      const img = f.type.includes("png")
        ? await pdfDoc.embedPng(bytes)
        : await pdfDoc.embedJpg(bytes);
      const page = pdfDoc.addPage([img.width, img.height]);
      page.drawImage(img, { x: 0, y: 0, width: img.width, height: img.height });
    }
    const pdfBytes = await pdfDoc.save();
    const blob = new Blob([pdfBytes], { type: "application/pdf" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "scanned.pdf";
    link.click();
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <h2>Tools</h2>

        <div className="tool-section">
          <h3>Background Remover</h3>
          <button onClick={handleRemoveBG}>Remove BG</button>
        </div>

        <div className="tool-section">
          <h3>Filters</h3>
          <select value={filter} onChange={(e) => setFilter(e.target.value)}>
            <option value="none">None</option>
            <option value="grayscale">Grayscale</option>
            <option value="sepia">Sepia</option>
            <option value="invert">Invert</option>
            <option value="blur">Blur</option>
            <option value="brightness">Brightness</option>
            <option value="contrast">Contrast</option>
            <option value="saturate">Saturate</option>
            <option value="hue-rotate">Hue Rotate</option>
          </select>
          <label>Intensity: {filterIntensity}%</label>
          <input
            type="range"
            min="0"
            max="100"
            value={filterIntensity}
            onChange={(e) => setFilterIntensity(Number(e.target.value))}
          />
        </div>

        <div className="tool-section">
          <h3>Compression</h3>
          <input
            type="range"
            min="10"
            max="100"
            value={compression}
            onChange={(e) => setCompression(Number(e.target.value))}
          />
          <span>{compression}%</span>
        </div>

        <div className="tool-section">
          <h3>Download</h3>
          <button onClick={() => handleDownloadImage("jpeg")}>JPEG</button>
          <button onClick={() => handleDownloadImage("png")}>PNG</button>
          <button onClick={() => handleDownloadImage("pdf")}>PDF</button>
        </div>

        <div className="tool-section">
          <h3>OCR Extractor</h3>
          <button onClick={handleOCR}>Run OCR</button>
        </div>
      </div>

      <div className="editor">
        {!imageURL ? (
          <div
            className="upload-zone"
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onClick={() => document.getElementById("fileInput").click()}
          >
            <p>Drag & Drop file(s) here or click to upload</p>
            <input
              id="fileInput"
              type="file"
              accept="image/*"
              multiple
              onChange={handleFileChange}
              style={{ display: "none" }}
            />
          </div>
        ) : (
          <>
            <img
              src={processedImage || imageURL}
              alt="Preview"
              style={{ maxWidth: "100%", ...getFilterStyle() }}
            />
            {/* Text Editor overlay */}
            <TextEditor onUpdate={(updatedBoxes) => setTextBoxes(updatedBoxes)} />
          </>
        )}

        {ocrText && (
          <div style={{ marginTop: 20 }}>
            <h3>OCR Extracted Text</h3>
            <textarea
              rows="8"
              value={ocrText}
              readOnly
              style={{ width: "100%" }}
            />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
