import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import { jsPDF } from "jspdf";
import { PDFDocument } from "pdf-lib";
import Tesseract from "tesseract.js";
import "./styles/App.css";

function App() {
  const [file, setFile] = useState(null);
  const [imageURL, setImageURL] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [ocrText, setOcrText] = useState("");
  const [filter, setFilter] = useState("none");
  const [filterIntensity, setFilterIntensity] = useState(100);
  const [compression, setCompression] = useState(100);
  const [addingText, setAddingText] = useState(false);
  const [currentText, setCurrentText] = useState("");
  const [placedTexts, setPlacedTexts] = useState([]);
  const [textStyle, setTextStyle] = useState({
    bold: false,
    italic: false,
    underline: false,
    uppercase: false,
    lowercase: false,
    color: "#000000",
    size: 20,
  });
  const [loadingOCR, setLoadingOCR] = useState(false);
  const [cvReady, setCvReady] = useState(false);

  const imageRef = useRef(null);

  // Load OpenCV.js
  useEffect(() => {
    const checkOpenCV = () => {
      if (window.cv && window.cv.imread) setCvReady(true);
      else setTimeout(checkOpenCV, 100);
    };
    checkOpenCV();
  }, []);

  // File upload
  const handleFileChange = (e) => {
    const uploaded = e.target.files[0];
    if (!uploaded) return;
    setFile(uploaded);
    setImageURL(URL.createObjectURL(uploaded));
    setProcessedImage(null);
    setOcrText("");
    setPlacedTexts([]);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const dropped = e.dataTransfer.files[0];
    if (!dropped) return;
    setFile(dropped);
    setImageURL(URL.createObjectURL(dropped));
    setProcessedImage(null);
    setOcrText("");
    setPlacedTexts([]);
  };
  const handleDragOver = (e) => e.preventDefault();

  // OCR
  const handleOCR = async () => {
    if (!processedImage && !file) return alert("Upload an image for OCR");
    const target = processedImage || imageURL;
    setLoadingOCR(true);
    Tesseract.recognize(target, "eng").then(({ data: { text } }) => {
      setOcrText(text);
      setLoadingOCR(false);
    });
  };

  // Background remover
  const handleRemoveBG = async () => {
    if (!file) return alert("Upload an image first");
    if (file.type.includes("pdf")) return alert("Works only with images");
    const formData = new FormData();
    formData.append("image_file", file);
    try {
      const res = await axios.post(
        "https://api.remove.bg/v1.0/removebg",
        formData,
        {
          headers: { "X-Api-Key": "VSNXBaMjvgBp6NjjRheLV3cW" },
          responseType: "blob",
        }
      );
      setProcessedImage(URL.createObjectURL(res.data));
    } catch (err) {
      alert("Remove.bg API error: " + err.message);
    }
  };

  // Filter
  const getFilterStyle = () => {
    const i = Number(filterIntensity);
    switch (filter) {
      case "grayscale":
        return { filter: `grayscale(${i}%)` };
      case "sepia":
        return { filter: `sepia(${i}%)` };
      case "invert":
        return { filter: `invert(${i}%)` };
      case "blur":
        return { filter: `blur(${i / 20}px)` };
      case "brightness":
        return { filter: `brightness(${i}%)` };
      case "contrast":
        return { filter: `contrast(${i}%)` };
      case "saturate":
        return { filter: `saturate(${i}%)` };
      case "hue-rotate":
        return { filter: `hue-rotate(${i * 3.6}deg)` };
      default:
        return {};
    }
  };

  // Generate final image
  const generateFinalImage = () =>
    new Promise((resolve) => {
      const source = processedImage || imageURL;
      if (!source) return resolve(null);
      const img = new Image();
      img.crossOrigin = "anonymous";
      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");
        canvas.width = img.width;
        canvas.height = img.height;
        ctx.filter = getFilterStyle().filter || "none";
        ctx.drawImage(img, 0, 0);
        resolve(canvas.toDataURL("image/jpeg", Number(compression) / 100));
      };
      img.src = source;
    });

  const handleDownloadImage = async (format) => {
    const finalDataURL = await generateFinalImage();
    if (!finalDataURL) return;
    if (format === "pdf") {
      const doc = new jsPDF();
      doc.addImage(finalDataURL, "JPEG", 10, 10, 190, 0);
      doc.save("docfixer.pdf");
    } else {
      const link = document.createElement("a");
      link.href = finalDataURL;
      link.download = `docfixer.${format}`;
      link.click();
    }
  };

  // Add text
  const handleAddText = () => setAddingText(true);
  const handlePlaceText = () => {
    setPlacedTexts([...placedTexts, { ...currentText }]);
    setCurrentText("");
    setAddingText(false);
  };
  const handleDeleteText = () => {
    setCurrentText("");
    setAddingText(false);
  };

  // DocScanner Pro: edge detection + perspective
const handleDocScanner = async () => {
  if (!file && !imageURL) return alert("Upload an image first");
  if (!cvReady) return alert("OpenCV is not loaded yet.");

  const imgElement = new Image();
  imgElement.src = imageURL;
  imgElement.onload = () => {
    const canvas = document.createElement("canvas");
    canvas.width = imgElement.width;
    canvas.height = imgElement.height;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(imgElement, 0, 0);

    let src = window.cv.imread(canvas);
    let dst = new window.cv.Mat();
    let gray = new window.cv.Mat();
    let blur = new window.cv.Mat();
    let edges = new window.cv.Mat();

    window.cv.cvtColor(src, gray, window.cv.COLOR_RGBA2GRAY, 0);
    window.cv.GaussianBlur(gray, blur, new window.cv.Size(5, 5), 0);
    window.cv.Canny(blur, edges, 50, 150);

    let contours = new window.cv.MatVector();
    let hierarchy = new window.cv.Mat();
    window.cv.findContours(edges, contours, hierarchy, window.cv.RETR_LIST, window.cv.CHAIN_APPROX_SIMPLE);

    let maxArea = 0;
    let pageContour = null;

    // Find the largest 4-point contour
    for (let i = 0; i < contours.size(); i++) {
      const cnt = contours.get(i);
      const area = window.cv.contourArea(cnt);
      if (area > maxArea) {
        const peri = window.cv.arcLength(cnt, true);
        const approx = new window.cv.Mat();
        window.cv.approxPolyDP(cnt, approx, 0.02 * peri, true);
        if (approx.rows === 4) {
          pageContour = approx;
          maxArea = area;
        } else {
          approx.delete();
        }
      }
    }

    if (!pageContour) {
      // Fallback: use whole image
      setProcessedImage(canvas.toDataURL());
      src.delete(); dst.delete(); gray.delete(); blur.delete(); edges.delete();
      contours.delete(); hierarchy.delete();
      return;
    }

    // Convert contour points to {x, y} objects
    const pts = [];
    for (let i = 0; i < 4; i++) {
      pts.push({
        x: pageContour.intPtr(i, 0)[0],
        y: pageContour.intPtr(i, 0)[1],
      });
    }

    // Order points: top-left, top-right, bottom-right, bottom-left
    pts.sort((a, b) => a.y - b.y);
    const top = pts.slice(0, 2).sort((a, b) => a.x - b.x);
    const bottom = pts.slice(2, 4).sort((a, b) => b.x - a.x);
    const rect = [top[0], top[1], bottom[1], bottom[0]];

    // Compute width & height
    const widthA = Math.hypot(rect[2].x - rect[3].x, rect[2].y - rect[3].y);
    const widthB = Math.hypot(rect[1].x - rect[0].x, rect[1].y - rect[0].y);
    const maxWidth = Math.max(widthA, widthB);

    const heightA = Math.hypot(rect[1].x - rect[2].x, rect[1].y - rect[2].y);
    const heightB = Math.hypot(rect[0].x - rect[3].x, rect[0].y - rect[3].y);
    const maxHeight = Math.max(heightA, heightB);

    const srcTri = window.cv.matFromArray(4, 1, window.cv.CV_32FC2, [
      rect[0].x, rect[0].y,
      rect[1].x, rect[1].y,
      rect[2].x, rect[2].y,
      rect[3].x, rect[3].y,
    ]);
    const dstTri = window.cv.matFromArray(4, 1, window.cv.CV_32FC2, [
      0, 0,
      maxWidth - 1, 0,
      maxWidth - 1, maxHeight - 1,
      0, maxHeight - 1,
    ]);

    const M = window.cv.getPerspectiveTransform(srcTri, dstTri);
    window.cv.warpPerspective(
      src,
      dst,
      M,
      new window.cv.Size(maxWidth, maxHeight),
      window.cv.INTER_LINEAR,
      window.cv.BORDER_CONSTANT,
      new window.cv.Scalar()
    );

    canvas.width = maxWidth;
    canvas.height = maxHeight;
    window.cv.imshow(canvas, dst);
    setProcessedImage(canvas.toDataURL());

    // Cleanup all mats
    src.delete(); dst.delete(); gray.delete(); blur.delete(); edges.delete();
    contours.delete(); hierarchy.delete(); pageContour.delete();
    srcTri.delete(); dstTri.delete(); M.delete();
  };
};



  return (
    <div className="app-container">
      <div className="sidebar">
        <h2>Tools</h2>

        <div className="tool-section">
          <h3>Background Remover</h3>
          <button onClick={handleRemoveBG}>Remove BG</button>
        </div>

        <div className="tool-section">
          <h3>Filters</h3>
          <select value={filter} onChange={(e) => setFilter(e.target.value)}>
            <option value="none">None</option>
            <option value="grayscale">Grayscale</option>
            <option value="sepia">Sepia</option>
            <option value="invert">Invert</option>
            <option value="blur">Blur</option>
            <option value="brightness">Brightness</option>
            <option value="contrast">Contrast</option>
            <option value="saturate">Saturate</option>
            <option value="hue-rotate">Hue Rotate</option>
          </select>
          <input
            type="range"
            min="0"
            max="100"
            value={filterIntensity}
            onChange={(e) => setFilterIntensity(Number(e.target.value))}
          />
        </div>

        <div className="tool-section">
          <h3>Compression</h3>
          <input
            type="range"
            min="10"
            max="100"
            value={compression}
            onChange={(e) => setCompression(Number(e.target.value))}
          />
          <span>{compression}%</span>
        </div>

        <div className="tool-section">
          <h3>Add Text</h3>
          <button onClick={handleAddText}>Add Text</button>
        </div>

        <div className="tool-section">
          <h3>DocScanner Pro</h3>
          <button onClick={handleDocScanner}>Scan Document</button>
        </div>

        <div className="tool-section">
          <h3>Download</h3>
          <button onClick={() => handleDownloadImage("jpeg")}>JPEG</button>
          <button onClick={() => handleDownloadImage("png")}>PNG</button>
          <button onClick={() => handleDownloadImage("pdf")}>PDF</button>
        </div>

        <div className="tool-section">
          <h3>OCR Extractor</h3>
          <button onClick={handleOCR}>{loadingOCR ? "Processing..." : "Run OCR"}</button>
        </div>
      </div>

      <div className="editor">
        {!imageURL ? (
          <div
            className="upload-zone"
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onClick={() => document.getElementById("fileInput").click()}
          >
            <p>Drag & Drop file(s) here or click to upload</p>
            <input
              id="fileInput"
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              style={{ display: "none" }}
            />
          </div>
        ) : (
          <div style={{ display: "flex", gap: "10px" }}>
            <div style={{ flex: 3, position: "relative" }}>
              <img
                src={processedImage || imageURL}
                alt="Preview"
                style={{ maxWidth: "100%", ...getFilterStyle() }}
                ref={imageRef}
              />

              {addingText && (
                <div
                  style={{
                    position: "absolute",
                    top: "50px",
                    left: "50px",
                    border: "1px dashed #000",
                    background: "#fff",
                    padding: "5px",
                    resize: "both",
                    overflow: "auto",
                  }}
                >
                  <textarea
                    value={currentText.text || ""}
                    onChange={(e) =>
                      setCurrentText({ ...currentText, text: e.target.value })
                    }
                    style={{
                      fontWeight: textStyle.bold ? "bold" : "normal",
                      fontStyle: textStyle.italic ? "italic" : "normal",
                      textDecoration: textStyle.underline ? "underline" : "none",
                      textTransform: textStyle.uppercase
                        ? "uppercase"
                        : textStyle.lowercase
                        ? "lowercase"
                        : "none",
                      color: textStyle.color,
                      fontSize: textStyle.size,
                      border: "none",
                      outline: "none",
                      background: "transparent",
                      width: "100%",
                    }}
                  />
                  <button onClick={handlePlaceText}>✔</button>
                  <button onClick={handleDeleteText}>✖</button>
                </div>
              )}

              {placedTexts.map((t, idx) => (
                <div
                  key={idx}
                  style={{
                    position: "absolute",
                    left: t.x || 50,
                    top: t.y || 50,
                    fontWeight: t.bold ? "bold" : "normal",
                    fontStyle: t.italic ? "italic" : "normal",
                    textDecoration: t.underline ? "underline" : "none",
                    textTransform: t.uppercase
                      ? "uppercase"
                      : t.lowercase
                      ? "lowercase"
                      : "none",
                    color: t.color,
                    fontSize: t.size,
                  }}
                >
                  {t.text}
                </div>
              ))}
            </div>

            <div
              style={{
                flex: 1,
                border: "1px solid #ccc",
                padding: "10px",
                display: "flex",
                flexDirection: "column",
              }}
            >
              {ocrText && (
                <>
                  <h3>OCR Output (Editable)</h3>
                  <textarea
                    value={ocrText}
                    onChange={(e) => setOcrText(e.target.value)}
                    style={{ width: "100%", flex: 1, resize: "none" }}
                  />
                </>
              )}

              {addingText && (
                <>
                  <h3>Text Tools</h3>
                  <button
                    onClick={() =>
                      setTextStyle({ ...textStyle, bold: !textStyle.bold })
                    }
                  >
                    Bold
                  </button>
                  <button
                    onClick={() =>
                      setTextStyle({ ...textStyle, italic: !textStyle.italic })
                    }
                  >
                    Italic
                  </button>
                  <button
                    onClick={() =>
                      setTextStyle({ ...textStyle, underline: !textStyle.underline })
                    }
                  >
                    Underline
                  </button>
                  <button
                    onClick={() =>
                      setTextStyle({
                        ...textStyle,
                        uppercase: !textStyle.uppercase,
                        lowercase: false,
                      })
                    }
                  >
                    Uppercase
                  </button>
                  <button
                    onClick={() =>
                      setTextStyle({
                        ...textStyle,
                        lowercase: !textStyle.lowercase,
                        uppercase: false,
                      })
                    }
                  >
                    Lowercase
                  </button>
                  <input
                    type="color"
                    value={textStyle.color}
                    onChange={(e) =>
                      setTextStyle({ ...textStyle, color: e.target.value })
                    }
                  />
                  <input
                    type="number"
                    value={textStyle.size}
                    onChange={(e) =>
                      setTextStyle({ ...textStyle, size: Number(e.target.value) })
                    }
                  />
                </>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
