import React, { useState } from "react";
import axios from "axios";
import "cropperjs/dist/cropper.css";
import "./styles/App.css";

function App() {
  const [file, setFile] = useState(null);
  const [imageURL, setImageURL] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [textOverlay, setTextOverlay] = useState("");
  const [filter, setFilter] = useState("none");
  const [compression, setCompression] = useState(100);

  // Handle file upload
  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    if (!uploadedFile) return;
    setFile(uploadedFile);
    setImageURL(URL.createObjectURL(uploadedFile));
    setProcessedImage(null);
  };

  // Remove.bg API call
  const handleRemoveBG = async () => {
    if (!file) return alert("Upload an image first");
    const formData = new FormData();
    formData.append("image_file", file);
    try {
      const res = await axios.post(
        "https://api.remove.bg/v1.0/removebg",
        formData,
        {
          headers: {
            "X-Api-Key": "VSNXBaMjvgBp6NjjRheLV3cW",
            "Content-Type": "multipart/form-data",
          },
          responseType: "blob",
        }
      );
      const url = URL.createObjectURL(res.data);
      setProcessedImage(url);
    } catch (err) {
      alert("Remove.bg API error: " + err.message);
    }
  };

  // Overlay text render
  const renderOverlay = () => {
    if (!textOverlay) return null;
    return (
      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          color: "#ff0000",
          fontSize: "24px",
          pointerEvents: "none",
        }}
      >
        {textOverlay}
      </div>
    );
  };

  // Filters
  const getFilterStyle = () => {
    switch (filter) {
      case "grayscale":
        return { filter: "grayscale(100%)" };
      case "sepia":
        return { filter: "sepia(100%)" };
      case "invert":
        return { filter: "invert(100%)" };
      default:
        return {};
    }
  };

  // Preview image for UI
  const getPreviewImage = () => {
    return processedImage || imageURL;
  };

  // Compressed data only for download
  const getCompressedData = () => {
    const source = processedImage || imageURL;
    if (!source) return null;
    const img = new Image();
    img.src = source;
    const canvas = document.createElement("canvas");
    const ctx = canvas.getContext("2d");
    canvas.width = img.width;
    canvas.height = img.height;
    ctx.drawImage(img, 0, 0);
    return canvas.toDataURL("image/jpeg", compression / 100);
  };

  // Download
  const handleDownload = (format) => {
    const dataURL = getCompressedData() || getPreviewImage();
    if (!dataURL) return;
    const link = document.createElement("a");
    link.href = dataURL;
    link.download = `docfixer.${format}`;
    link.click();
  };

  return (
    <div className="app-container">
      {/* Left tools panel */}
      <div className="tools-panel">
        <h1>DocFixer</h1>
        <input
          type="file"
          accept="image/*,application/pdf"
          onChange={handleFileChange}
        />

        <div className="component-wrapper">
          <h2>Background Remover</h2>
          <button onClick={handleRemoveBG}>Remove Background</button>
        </div>

        <div className="component-wrapper">
          <h2>Document Editor</h2>
          <input
            type="text"
            placeholder="Add text overlay"
            value={textOverlay}
            onChange={(e) => setTextOverlay(e.target.value)}
          />
        </div>

        <div className="component-wrapper">
          <h2>Filters</h2>
          <select value={filter} onChange={(e) => setFilter(e.target.value)}>
            <option value="none">None</option>
            <option value="grayscale">Grayscale</option>
            <option value="sepia">Sepia</option>
            <option value="invert">Invert</option>
          </select>
        </div>

        <div className="component-wrapper">
          <h2>Compression</h2>
          <div className="range-slider">
            <input
              type="range"
              min="10"
              max="100"
              value={compression}
              onChange={(e) => setCompression(e.target.value)}
            />
            <span>{compression}%</span>
          </div>
        </div>

        <div className="component-wrapper">
          <h2>Download</h2>
          <button onClick={() => handleDownload("jpeg")}>Download JPEG</button>
          <button onClick={() => handleDownload("png")}>Download PNG</button>
          <button onClick={() => handleDownload("pdf")}>Download PDF</button>
        </div>
      </div>

      {/* Right preview panel */}
      <div className="preview-panel">
        <h2>Preview</h2>
        <div style={{ position: "relative", textAlign: "center" }}>
          {getPreviewImage() ? (
            <img
              src={getPreviewImage()}
              alt="Preview"
              style={{ maxWidth: "100%", ...getFilterStyle() }}
            />
          ) : (
            <p>Upload a document to preview</p>
          )}
          {renderOverlay()}
        </div>
      </div>
    </div>
  );
}

export default App;
