import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { jsPDF } from "jspdf";
import { PDFDocument } from "pdf-lib";
import Tesseract from "tesseract.js";
import { HexColorPicker } from "react-colorful";
import "./styles/App.css";

function App() {
  const [file, setFile] = useState(null);
  const [imageURL, setImageURL] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [filter, setFilter] = useState("none");
  const [filterIntensity, setFilterIntensity] = useState(100);
  const [compression, setCompression] = useState(100);
  const [texts, setTexts] = useState([]);
  const [selectedTextId, setSelectedTextId] = useState(null);
  const [showTextTools, setShowTextTools] = useState(false);
  const [ocrText, setOcrText] = useState("");
  const [scannedFiles, setScannedFiles] = useState([]);

  // Inline editing state
  const [editingTextId, setEditingTextId] = useState(null);
  const [tempText, setTempText] = useState("");
  const inputRef = useRef(null);

  useEffect(() => {
    if (editingTextId) {
      setTimeout(() => inputRef.current?.focus(), 0);
    }
  }, [editingTextId]);

  // ===== File Handling =====
  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    if (!uploadedFile) return;
    setFile(uploadedFile);
    setImageURL(URL.createObjectURL(uploadedFile));
    setProcessedImage(null);
    setTexts([]);
    setOcrText("");
    setSelectedTextId(null);
    setShowTextTools(false);
    setEditingTextId(null);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    if (!droppedFile) return;
    setFile(droppedFile);
    setImageURL(URL.createObjectURL(droppedFile));
    setProcessedImage(null);
    setTexts([]);
    setOcrText("");
    setSelectedTextId(null);
    setShowTextTools(false);
    setEditingTextId(null);
  };

  const handleDragOver = (e) => e.preventDefault();

  // ===== Remove Background =====
  const handleRemoveBG = async () => {
    if (!file) return alert("Upload an image first");
    if (file.type.includes("pdf"))
      return alert("Remove.bg works only with images");

    const formData = new FormData();
    formData.append("image_file", file);
    try {
      const res = await axios.post(
        "https://api.remove.bg/v1.0/removebg",
        formData,
        {
          headers: { "X-Api-Key": "VSNXBaMjvgBp6NjjRheLV3cW" },
          responseType: "blob",
        }
      );
      setProcessedImage(URL.createObjectURL(res.data));
    } catch (err) {
      alert("Remove.bg API error: " + err.message);
    }
  };

  // ===== Filters =====
  const getFilterStyle = () => {
    const i = Number(filterIntensity);
    switch (filter) {
      case "grayscale":
        return { filter: `grayscale(${i}%)` };
      case "sepia":
        return { filter: `sepia(${i}%)` };
      case "invert":
        return { filter: `invert(${i}%)` };
      case "blur":
        return { filter: `blur(${i / 20}px)` };
      case "brightness":
        return { filter: `brightness(${i}%)` };
      case "contrast":
        return { filter: `contrast(${i}%)` };
      case "saturate":
        return { filter: `saturate(${i}%)` };
      case "hue-rotate":
        return { filter: `hue-rotate(${i * 3.6}deg)` };
      default:
        return {};
    }
  };

  // ===== Text Overlay =====
  const addText = () => {
    const newText = {
      id: Date.now(),
      text: "New Text",
      color: "#ff0000",
      fontSize: 24,
      fontWeight: 400,
      x: 50,
      y: 50,
      rotation: 0,
      opacity: 1,
    };
    setTexts((prev) => [...prev, newText]);
    setSelectedTextId(newText.id);
    setShowTextTools(true);
  };

  const updateText = (id, key, value) => {
    setTexts((prev) => prev.map((t) => (t.id === id ? { ...t, [key]: value } : t)));
  };

  const deleteText = (id) => {
    setTexts((prev) => prev.filter((t) => t.id !== id));
    if (selectedTextId === id) setSelectedTextId(null);
    if (editingTextId === id) setEditingTextId(null);
  };

  // ===== Generate Image =====
  const generateFinalImage = () => {
    return new Promise((resolve) => {
      const source = processedImage || imageURL;
      if (!source) return resolve(null);
      const img = new Image();
      img.crossOrigin = "anonymous";
      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");
        canvas.width = img.width;
        canvas.height = img.height;
        ctx.filter = getFilterStyle().filter || "none";
        ctx.drawImage(img, 0, 0);
        texts.forEach((t) => {
          ctx.save();
          ctx.translate(t.x, t.y);
          ctx.rotate((t.rotation * Math.PI) / 180);
          ctx.globalAlpha = t.opacity ?? 1;
          ctx.font = `${t.fontWeight} ${t.fontSize}px Arial`;
          ctx.fillStyle = t.color || "#000";
          ctx.textAlign = "left";
          ctx.textBaseline = "top";
          ctx.fillText(t.text, 0, 0);
          ctx.restore();
        });
        resolve(canvas.toDataURL("image/jpeg", Number(compression) / 100));
      };
      img.src = source;
    });
  };

  // ===== Download =====
  const handleDownloadImage = async (format) => {
    const finalDataURL = await generateFinalImage();
    if (!finalDataURL) return;
    if (format === "pdf") {
      const doc = new jsPDF();
      doc.addImage(finalDataURL, "JPEG", 10, 10, 190, 0);
      doc.save("docfixer.pdf");
    } else {
      const link = document.createElement("a");
      link.href = finalDataURL;
      link.download = `docfixer.${format}`;
      link.click();
    }
  };

  // ===== OCR =====
  const handleOCR = async () => {
    if (!file || !file.type.startsWith("image/"))
      return alert("Upload an image for OCR");
    setOcrText("Processing...");
    Tesseract.recognize(file, "eng").then(({ data: { text } }) => setOcrText(text));
  };

  // ===== Doc Scanner =====
  const handleDocScanner = async () => {
    if (!scannedFiles.length) return alert("No images scanned yet");
    const pdfDoc = await PDFDocument.create();
    for (let imgFile of scannedFiles) {
      const bytes = await imgFile.arrayBuffer();
      const img = imgFile.type.includes("png")
        ? await pdfDoc.embedPng(bytes)
        : await pdfDoc.embedJpg(bytes);
      const page = pdfDoc.addPage([img.width, img.height]);
      page.drawImage(img, { x: 0, y: 0, width: img.width, height: img.height });
    }
    const pdfBytes = await pdfDoc.save();
    const blob = new Blob([pdfBytes], { type: "application/pdf" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "scanned.pdf";
    link.click();
  };

  const addScannedFile = (e) => setScannedFiles((prev) => [...prev, ...Array.from(e.target.files)]);

  // Helpers
  const saveEditedText = (id) => {
    updateText(id, "text", tempText);
    setEditingTextId(null);
  };

  const cancelEditing = () => {
    setTempText("");
    setEditingTextId(null);
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <h2>Tools</h2>

        <div className="tool-section">
          <h3>Background Remover</h3>
          <button onClick={handleRemoveBG}>Remove BG</button>
        </div>

        <div className="tool-section">
          <h3>Text Overlay</h3>
          <button onClick={addText}>+ Add Text</button>
        </div>

        <div className="tool-section">
          <h3>Filters</h3>
          <select value={filter} onChange={(e) => setFilter(e.target.value)}>
            <option value="none">None</option>
            <option value="grayscale">Grayscale</option>
            <option value="sepia">Sepia</option>
            <option value="invert">Invert</option>
            <option value="blur">Blur</option>
            <option value="brightness">Brightness</option>
            <option value="contrast">Contrast</option>
            <option value="saturate">Saturate</option>
            <option value="hue-rotate">Hue Rotate</option>
          </select>
          <label>Intensity: {filterIntensity}%</label>
          <input
            type="range"
            min="0"
            max="100"
            value={filterIntensity}
            onChange={(e) => setFilterIntensity(Number(e.target.value))}
          />
        </div>

        <div className="tool-section">
          <h3>Compression</h3>
          <input
            type="range"
            min="10"
            max="100"
            value={compression}
            onChange={(e) => setCompression(Number(e.target.value))}
          />
          <span>{compression}%</span>
        </div>

        <div className="tool-section">
          <h3>Download</h3>
          <button onClick={() => handleDownloadImage("jpeg")}>JPEG</button>
          <button onClick={() => handleDownloadImage("png")}>PNG</button>
          <button onClick={() => handleDownloadImage("pdf")}>PDF</button>
        </div>

        <div className="tool-section">
          <h3>OCR Extractor</h3>
          <button onClick={handleOCR}>Run OCR</button>
        </div>

        <div className="tool-section">
          <h3>Doc Scanner</h3>
          <input type="file" multiple onChange={addScannedFile} />
          <button onClick={handleDocScanner}>Create PDF</button>
        </div>
      </div>

      <div
        className="editor"
        onClick={() => {
          setSelectedTextId(null);
          setShowTextTools(false);
          setEditingTextId(null);
        }}
      >
        {!imageURL ? (
          <div
            className="upload-zone"
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onClick={(e) => {
              e.stopPropagation();
              document.getElementById("fileInput").click();
            }}
          >
            <p>Drag & Drop file here or click to upload</p>
            <input
              id="fileInput"
              type="file"
              accept="image/*,application/pdf"
              onChange={handleFileChange}
              style={{ display: "none" }}
            />
          </div>
        ) : (
          <div style={{ display: "flex", gap: "10px" }}>
            <div
              style={{
                flex: 3,
                position: "relative",
                minHeight: "500px",
                border: "1px solid #ccc",
                overflow: "hidden",
              }}
              onClick={(e) => e.stopPropagation()}
            >
              <img
                src={processedImage || imageURL}
                alt="Preview"
                style={{
                  maxWidth: "100%",
                  display: "block",
                  ...getFilterStyle(),
                }}
                onClick={(e) => e.stopPropagation()}
              />

              {texts.map((t) => {
                const isSelected = selectedTextId === t.id;
                const isEditing = editingTextId === t.id;
                return (
                  <div
                    key={t.id}
                    onClick={(e) => {
                      e.stopPropagation();
                      setSelectedTextId(t.id);
                      setShowTextTools(true);
                    }}
                    draggable
                    onDragStart={(e) => {
                      e.dataTransfer.setData("text/plain", t.id);
                      setSelectedTextId(t.id);
                      setShowTextTools(true);
                    }}
                    onDragEnd={(e) => {
                      e.stopPropagation();
                      const rect = e.currentTarget.parentElement.getBoundingClientRect();
                      const x = Math.max(0, e.clientX - rect.left - 50);
                      const y = Math.max(0, e.clientY - rect.top - 20);
                      updateText(t.id, "x", x);
                      updateText(t.id, "y", y);
                    }}
                    onDoubleClick={(e) => {
                      e.stopPropagation();
                      setSelectedTextId(t.id);
                      setShowTextTools(true);
                      setEditingTextId(t.id);
                      setTempText(t.text);
                    }}
                    style={{
                      position: "absolute",
                      left: t.x,
                      top: t.y,
                      color: t.color,
                      fontSize: t.fontSize,
                      fontWeight: t.fontWeight,
                      transform: `rotate(${t.rotation}deg)`,
                      opacity: t.opacity,
                      cursor: "move",
                      userSelect: "none",
                      pointerEvents: "auto",
                      border: isSelected ? "2px dashed #007bff" : "none",
                      padding: "6px 8px",
                      borderRadius: "6px",
                      backgroundColor: isSelected ? "rgba(0,123,255,0.06)" : "transparent",
                      zIndex: 5,
                      minWidth: 20,
                      minHeight: 20,
                    }}
                  >
                    {isSelected && isEditing && (
                      <button
                        onClick={(ev) => {
                          ev.stopPropagation();
                          saveEditedText(t.id);
                        }}
                        title="Save"
                        style={{
                          position: "absolute",
                          top: -10,
                          left: -10,
                          backgroundColor: "#28a745",
                          color: "#fff",
                          borderRadius: "50%",
                          width: 22,
                          height: 22,
                          display: "flex",
                          alignItems: "center",
                          justifyContent: "center",
                          cursor: "pointer",
                          border: "none",
                          zIndex: 10,
                          fontSize: 12,
                          fontWeight: "bold",
                        }}
                      >
                        
                      </button>
                    )}

                    {isSelected && (
                      <button
                        onClick={(ev) => {
                          ev.stopPropagation();
                          deleteText(t.id);
                        }}
                        title="Delete"
                        style={{
                          position: "absolute",
                          top: -10,
                          right: -10,
                          backgroundColor: "#ff4d4f",
                          color: "#fff",
                          borderRadius: "50%",
                          width: 22,
                          height: 22,
                          display: "flex",
                          alignItems: "center",
                          justifyContent: "center",
                          cursor: "pointer",
                          border: "none",
                          zIndex: 10,
                          fontSize: 12,
                          fontWeight: "bold",
                        }}
                      >
                        
                      </button>
                    )}

                    {isEditing ? (
                      <input
                        ref={inputRef}
                        type="text"
                        value={tempText}
                        onChange={(e) => setTempText(e.target.value)}
                        onClick={(e) => e.stopPropagation()}
                        onKeyDown={(e) => {
                          if (e.key === "Enter") {
                            e.preventDefault();
                            saveEditedText(t.id);
                          } else if (e.key === "Escape") {
                            e.preventDefault();
                            cancelEditing();
                          }
                        }}
                        style={{
                          padding: "4px 6px",
                          borderRadius: 4,
                          border: "1px solid #ccc",
                          minWidth: 100,
                          background: "#fff",
                          zIndex: 9,
                        }}
                      />
                    ) : (
                      <span style={{ pointerEvents: "none" }}>{t.text}</span>
                    )}
                  </div>
                );
              })}
            </div>

            {showTextTools && selectedTextId && (
              <div style={{ flex: 1, border: "1px solid #ccc", padding: 10 }}>
                <h3>Text Options</h3>
                <label>
                  Font Size:
                  <input
                    type="number"
                    value={texts.find((t) => t.id === selectedTextId)?.fontSize || 24}
                    onChange={(e) =>
                      updateText(selectedTextId, "fontSize", parseInt(e.target.value || 24))
                    }
                    style={{ width: 60, marginLeft: 8 }}
                  />
                </label>

                <div style={{ marginTop: 10 }}>
                  <label>
                    Text Thickness:{" "}
                    {texts.find((t) => t.id === selectedTextId)?.fontWeight || 400}
                  </label>
                  <input
                    type="range"
                    min="100"
                    max="900"
                    step="100"
                    value={texts.find((t) => t.id === selectedTextId)?.fontWeight || 400}
                    onChange={(e) =>
                      updateText(selectedTextId, "fontWeight", parseInt(e.target.value || 400))
                    }
                    style={{ width: "100%", marginTop: 5 }}
                  />
                </div>

                <div style={{ marginTop: 10 }}>
                  <label>
                    Rotation:{" "}
                    {texts.find((t) => t.id === selectedTextId)?.rotation || 0}
                  </label>
                  <input
                    type="range"
                    min="0"
                    max="360"
                    value={texts.find((t) => t.id === selectedTextId)?.rotation || 0}
                    onChange={(e) =>
                      updateText(selectedTextId, "rotation", parseInt(e.target.value || 0))
                    }
                    style={{ width: "100%", marginTop: 5 }}
                  />
                </div>

                <div style={{ marginTop: 10 }}>
                  <label>
                    Transparency:{" "}
                    {(texts.find((t) => t.id === selectedTextId)?.opacity || 1) * 100}%
                  </label>
                  <input
                    type="range"
                    min="0"
                    max="100"
                    value={(texts.find((t) => t.id === selectedTextId)?.opacity || 1) * 100}
                    onChange={(e) =>
                      updateText(
                        selectedTextId,
                        "opacity",
                        parseInt(e.target.value || 100) / 100
                      )
                    }
                    style={{ width: "100%", marginTop: 5 }}
                  />
                </div>

                <div style={{ marginTop: 10 }}>
                  <label>Text Color:</label>
                  <HexColorPicker
                    color={texts.find((t) => t.id === selectedTextId)?.color || "#000"}
                    onChange={(color) => updateText(selectedTextId, "color", color)}
                  />
                </div>
              </div>
            )}
          </div>
        )}

        {ocrText && (
          <div style={{ marginTop: 20 }}>
            <h3>OCR Extracted Text</h3>
            <textarea rows="8" value={ocrText} readOnly style={{ width: "100%" }} />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
