import React, { useState, useRef } from "react";
import axios from "axios";
// FIX: Using esm.sh for CDN imports to resolve build errors
import Rnd from 'https://esm.sh/react-rnd';
import jsPDF from 'https://esm.sh/jspdf';
import { PDFDocument } from 'https://esm.sh/pdf-lib';
import Tesseract from 'https://esm.sh/tesseract.js';
import { HexColorPicker } from "react-colorful";

// FIX: CSS is now embedded directly to resolve the import error.
const AppStyles = `
/* General App Layout */
body {
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
  margin: 0;
  padding: 0;
  background-color: #f0f2f5;
  color: #333;
}

.app-container {
  display: flex;
  height: 100vh;
}

/* Sidebar Styling */
.sidebar {
  width: 280px;
  background-color: #ffffff;
  padding: 20px;
  box-shadow: 2px 0 5px rgba(0,0,0,0.1);
  overflow-y: auto;
  z-index: 10;
}

.sidebar h2 {
  text-align: center;
  margin-top: 0;
  color: #007bff;
  border-bottom: 2px solid #eee;
  padding-bottom: 10px;
}

.tool-section {
  margin-bottom: 25px;
  border-bottom: 1px solid #e0e0e0;
  padding-bottom: 20px;
}

.tool-section:last-child {
  border-bottom: none;
}

.tool-section h3 {
  margin-top: 0;
  margin-bottom: 15px;
  font-size: 1rem;
  color: #555;
}

.sidebar button,
.sidebar input[type="file"]::file-selector-button {
  width: 100%;
  padding: 10px;
  margin-bottom: 10px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.2s;
  font-size: 0.9rem;
}

.sidebar button:hover {
  background-color: #0056b3;
}

.sidebar select,
.sidebar input[type="range"],
.sidebar input[type="number"] {
  width: 100%;
  margin-top: 5px;
  margin-bottom: 5px;
}

.sidebar label {
  display: block;
  margin-bottom: 5px;
  font-size: 0.9rem;
}

/* Main Editor Area */
.editor {
  flex-grow: 1;
  padding: 20px;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #e9ecef;
  overflow: auto;
}

.upload-zone {
  width: 80%;
  max-width: 600px;
  height: 300px;
  border: 2px dashed #adb5bd;
  border-radius: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  background-color: #f8f9fa;
  text-align: center;
  color: #6c757d;
  transition: background-color 0.2s, border-color 0.2s;
}

.upload-zone:hover {
  background-color: #e2e6ea;
  border-color: #007bff;
}

/* Layout when image is loaded */
.editor-layout {
  display: flex;
  width: 100%;
  height: 100%;
  gap: 20px;
}

.image-container {
  flex: 3;
  position: relative;
  min-height: 500px;
  background-color: #fff;
  border: 1px solid #ccc;
  border-radius: 8px;
  overflow: hidden; /* Important for Rnd bounds */
  display: flex;
  justify-content: center;
  align-items: center;
}

.image-container img {
    max-width: 100%;
    max-height: 100%;
    object-fit: contain;
}

.right-panel {
  flex: 1;
  border: 1px solid #ccc;
  border-radius: 8px;
  padding: 15px;
  background-color: #fff;
  overflow-y: auto;
}

.text-tools h3, .ocr-output h3 {
    margin-top: 0;
}

.text-tools label {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.text-tools input[type="number"] {
    width: 60px;
    padding: 5px;
}

.ocr-output pre {
    white-space: pre-wrap;
    word-break: break-word;
    background-color: #f8f9fa;
    padding: 10px;
    border-radius: 5px;
}
.placeholder-panel {
    color: #888;
    text-align: center;
    padding-top: 20px;
}

/* Text Box (Rnd Component) Styling */
.selected-text {
  border: 2px dashed #007bff;
  z-index: 5 !important; /* Ensure selected is on top */
}

.delete-handle {
  position: absolute;
  top: -10px;
  left: -10px;
  width: 20px;
  height: 20px;
  background-color: #ff4d4f;
  color: #fff;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 12px;
  font-weight: bold;
  z-index: 10;
  border: 1px solid white;
}
`;

function App() {
  const [file, setFile] = useState(null);
  const [imageURL, setImageURL] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [filter, setFilter] = useState("none");
  const [filterIntensity, setFilterIntensity] = useState(100);
  const [compression, setCompression] = useState(100);
  const [texts, setTexts] = useState([]);
  const [selectedTextId, setSelectedTextId] = useState(null);
  const [showTextTools, setShowTextTools] = useState(false);
  const [ocrText, setOcrText] = useState("");
  const [scannedFiles, setScannedFiles] = useState([]);
  const editorRef = useRef(null); // Ref for the editor area

  // ===== File Handling =====
  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    if (!uploadedFile) return;
    setFile(uploadedFile);
    setImageURL(URL.createObjectURL(uploadedFile));
    setProcessedImage(null);
    setTexts([]);
    setOcrText("");
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    if (!droppedFile) return;
    setFile(droppedFile);
    setImageURL(URL.createObjectURL(droppedFile));
    setProcessedImage(null);
    setTexts([]);
    setOcrText("");
  };

  const handleDragOver = (e) => e.preventDefault();

  // ===== Background Remover =====
  const handleRemoveBG = async () => {
    if (!file) return alert("Upload an image first");
    if (file.type.includes("pdf")) return alert("Remove.bg works only with images");

    const formData = new FormData();
    formData.append("image_file", file);
    try {
      const res = await axios.post(
        "https://api.remove.bg/v1.0/removebg",
        formData,
        { headers: { "X-Api-Key": "VSNXBaMjvgBp6NjjRheLV3cW" }, responseType: "blob" }
      );
      setProcessedImage(URL.createObjectURL(res.data));
    } catch (err) {
      alert("Remove.bg API error: " + err.message);
    }
  };

  // ===== Filters =====
  const getFilterStyle = () => {
    const i = filterIntensity;
    switch (filter) {
      case "grayscale": return { filter: `grayscale(${i}%)` };
      case "sepia": return { filter: `sepia(${i}%)` };
      case "invert": return { filter: `invert(${i}%)` };
      case "blur": return { filter: `blur(${i / 20}px)` };
      case "brightness": return { filter: `brightness(${i}%)` };
      case "contrast": return { filter: `contrast(${i}%)` };
      case "saturate": return { filter: `saturate(${i}%)` };
      case "hue-rotate": return { filter: `hue-rotate(${i * 3.6}deg)` };
      default: return {};
    }
  };

  // ===== Text Overlay =====
  const addText = () => {
    const newText = {
      id: Date.now(),
      text: "New Text",
      color: "#ff0000",
      fontSize: 24,
      x: 50,
      y: 50,
      width: 200,
      height: 100, // Increased default height for better visibility
      rotation: 0,
      opacity: 1,
    };
    setTexts([...texts, newText]);
    setSelectedTextId(newText.id);
    setShowTextTools(true);
  };

  const updateText = (id, key, value) => {
    setTexts(texts.map((t) => (t.id === id ? { ...t, [key]: value } : t)));
  };

  const deleteText = (id) => {
    setTexts(texts.filter((t) => t.id !== id));
    if (selectedTextId === id) setSelectedTextId(null);
  };

  // NEW: Deselect text box when clicking outside
  const handleCanvasClick = (e) => {
    // Check if the click is on the container itself and not on a text box
    if (e.target === e.currentTarget) {
      setSelectedTextId(null);
    }
  };


  // ===== Generate Image =====
  const generateFinalImage = () => {
    return new Promise((resolve) => {
      const source = processedImage || imageURL;
      if (!source) return resolve(null);
      const img = new Image();
      img.crossOrigin = "anonymous";
      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");
        canvas.width = img.width;
        canvas.height = img.height;
        ctx.filter = getFilterStyle().filter || "none";
        ctx.drawImage(img, 0, 0);
        texts.forEach((t) => {
          ctx.save();
          ctx.translate(t.x + t.width / 2, t.y + t.height / 2);
          ctx.rotate((t.rotation * Math.PI) / 180);
          ctx.globalAlpha = t.opacity;
          ctx.font = `${t.fontSize}px Arial`;
          ctx.fillStyle = t.color;
          ctx.textAlign = "center";
          ctx.textBaseline = "middle";
          // Handle multiline text
          const lines = t.text.split('\n');
          lines.forEach((line, index) => {
            const yPos = index * t.fontSize - ((lines.length - 1) * t.fontSize / 2);
            ctx.fillText(line, 0, yPos);
          });
          ctx.restore();
        });
        resolve(canvas.toDataURL("image/jpeg", compression / 100));
      };
      img.src = source;
    });
  };

  // ===== Download =====
  const handleDownloadImage = async (format) => {
    const finalDataURL = await generateFinalImage();
    if (!finalDataURL) return;
    if (format === "pdf") {
      const doc = new jsPDF();
      doc.addImage(finalDataURL, "JPEG", 10, 10, 190, 0);
      doc.save("docfixer.pdf");
    } else {
      const link = document.createElement("a");
      link.href = finalDataURL;
      link.download = `docfixer.${format}`;
      link.click();
    }
  };

  // ===== OCR =====
  const handleOCR = async () => {
    if (!file || !file.type.startsWith("image/")) return alert("Upload an image for OCR");
    setOcrText("Processing...");
    Tesseract.recognize(file, "eng").then(({ data: { text } }) => setOcrText(text));
  };

  // ===== Doc Scanner =====
  const handleDocScanner = async () => {
    if (!scannedFiles.length) return alert("No images scanned yet");
    const pdfDoc = await PDFDocument.create();
    for (let imgFile of scannedFiles) {
      const bytes = await imgFile.arrayBuffer();
      const img = imgFile.type.includes("png") ? await pdfDoc.embedPng(bytes) : await pdfDoc.embedJpg(bytes);
      const page = pdfDoc.addPage([img.width, img.height]);
      page.drawImage(img, { x: 0, y: 0, width: img.width, height: img.height });
    }
    const pdfBytes = await pdfDoc.save();
    const blob = new Blob([pdfBytes], { type: "application/pdf" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "scanned.pdf";
    link.click();
  };

  const addScannedFile = (e) => setScannedFiles([...scannedFiles, ...Array.from(e.target.files)]);
  
  const currentText = texts.find((t) => t.id === selectedTextId);

  return (
    <>
      <style>{AppStyles}</style>
      <div className="app-container">
        <div className="sidebar">
          <h2>Tools</h2>
          <div className="tool-section">
            <h3>Background Remover</h3>
            <button onClick={handleRemoveBG}>Remove BG</button>
          </div>
          <div className="tool-section">
            <h3>Text Overlay</h3>
            <button onClick={addText}>+ Add Text</button>
          </div>
          <div className="tool-section">
            <h3>Filters</h3>
            <select value={filter} onChange={(e) => setFilter(e.target.value)}>
              <option value="none">None</option>
              <option value="grayscale">Grayscale</option>
              <option value="sepia">Sepia</option>
              <option value="invert">Invert</option>
              <option value="blur">Blur</option>
              <option value="brightness">Brightness</option>
              <option value="contrast">Contrast</option>
              <option value="saturate">Saturate</option>
              <option value="hue-rotate">Hue Rotate</option>
            </select>
            <label>Intensity: {filterIntensity}%</label>
            <input type="range" min="0" max="100" value={filterIntensity} onChange={(e) => setFilterIntensity(e.target.value)} />
          </div>
          <div className="tool-section">
            <h3>Compression</h3>
            <input type="range" min="10" max="100" value={compression} onChange={(e) => setCompression(e.target.value)} />
            <span>{compression}%</span>
          </div>
          <div className="tool-section">
            <h3>Download</h3>
            <button onClick={() => handleDownloadImage("jpeg")}>JPEG</button>
            <button onClick={() => handleDownloadImage("png")}>PNG</button>
            <button onClick={() => handleDownloadImage("pdf")}>PDF</button>
          </div>
          <div className="tool-section">
            <h3>OCR Extractor</h3>
            <button onClick={handleOCR}>Run OCR</button>
          </div>
          <div className="tool-section">
            <h3>Doc Scanner</h3>
            <input type="file" multiple onChange={addScannedFile} />
            <button onClick={handleDocScanner}>Create PDF</button>
          </div>
        </div>

        <div className="editor" ref={editorRef}>
          {!imageURL ? (
            <div
              className="upload-zone"
              onDrop={handleDrop}
              onDragOver={handleDragOver}
              onClick={() => document.getElementById("fileInput").click()}
            >
              <p>Drag & Drop file here or click to upload</p>
              <input id="fileInput" type="file" accept="image/*,application/pdf" onChange={handleFileChange} style={{ display: "none" }} />
            </div>
          ) : (
            <div className="editor-layout">
              <div 
                className="image-container"
                onClick={handleCanvasClick} // Deselect on background click
              >
                {file?.type.startsWith("image/") ? (
                  <img src={processedImage || imageURL} alt="Preview" style={{ maxWidth: "100%", display: "block", ...getFilterStyle() }} />
                ) : (
                  <embed src={imageURL} type="application/pdf" width="100%" height="600px" />
                )}
                {texts.map((t) => (
                  <Rnd
                    key={t.id}
                    size={{ width: t.width, height: t.height }}
                    position={{ x: t.x, y: t.y }}
                    bounds="parent"
                    onDragStop={(e, d) => {
                      updateText(t.id, "x", d.x);
                      updateText(t.id, "y", d.y);
                    }}
                    onResizeStop={(e, dir, ref, delta, pos) => {
                      updateText(t.id, "width", parseInt(ref.style.width));
                      updateText(t.id, "height", parseInt(ref.style.height));
                      updateText(t.id, "x", pos.x);
                      updateText(t.id, "y", pos.y);
                    }}
                    onClick={(e) => {
                      e.stopPropagation(); // Prevent canvas click from firing
                      setSelectedTextId(t.id);
                    }}
                    className={selectedTextId === t.id ? "selected-text" : ""}
                  >
                    {selectedTextId === t.id && (
                      <div
                        className="delete-handle"
                        onClick={(e) => {
                          e.stopPropagation();
                          deleteText(t.id);
                        }}
                      >
                        ×
                      </div>
                    )}
                    <textarea
                      value={t.text}
                      onChange={(e) => updateText(t.id, "text", e.target.value)}
                      style={{
                        width: "100%",
                        height: "100%",
                        color: t.color,
                        fontSize: `${t.fontSize}px`,
                        backgroundColor: 'transparent',
                        border: 'none',
                        outline: 'none',
                        resize: 'none',
                        textAlign: 'center',
                        fontFamily: 'Arial',
                        whiteSpace: 'pre-wrap',
                        overflowWrap: 'break-word',
                      }}
                    />
                  </Rnd>
                ))}
              </div>

              {/* Right Panel for OCR or Text Tools */}
              <div className="right-panel">
                  {ocrText ? (
                      <div className="ocr-output">
                          <h3>OCR Output</h3>
                          <pre>{ocrText}</pre>
                      </div>
                  ) : showTextTools && currentText ? (
                      <div className="text-tools">
                          <h3>Text Options</h3>
                          <label>
                              Font Size:
                              <input
                                  type="number"
                                  value={currentText.fontSize}
                                  onChange={(e) => updateText(selectedTextId, "fontSize", parseInt(e.target.value))}
                              />
                          </label>
                          <div style={{ marginTop: 10 }}>
                              <h4>Color:</h4>
                              <HexColorPicker
                                  color={currentText.color}
                                  onChange={(color) => updateText(selectedTextId, "color", color)}
                              />
                          </div>
                      </div>
                  ) : (
                      <div className="placeholder-panel">Select a text box to see its options or run OCR.</div>
                  )}
              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
}

export default App;
